INSERT INTO `k_company_info` (`cpid`, `info`, `mainbissness`, `team`, `voucher`, `contact_us`, `charge`, `uid`, `boss_name`, `boss_mobile`, `operation_name`, `operation_mobile`, `dateline`) VALUES
(46, '<p class="p0" style="color:#474E5D;font-family:''microsoft yahei'', Tahoma, Helvetica, Arial, sans-serif;font-size:14px;background-color:#FFFFFF;">\r\n	<span style="font-family:宋体;font-size:10.5pt;"><span style="font-family:''Microsoft YaHei'';">借力温州金融改革的历史机遇，自公司成立三年来，致力于二手车贷款、房产抵押贷款、投资理财贷款等相关业务，在业内一直处于领先地位，成为互联网金融发展最快的公司之一，目前已和数</span><span style="font-family:''Microsoft YaHei'';">10</span><span style="font-family:''Microsoft YaHei'';">家融资担保公司和小额担保公司建立了合作伙伴关系。据第三方机构网贷之家数据排名显示，</span><span style="font-family:''Microsoft YaHei'';">2013</span><span style="font-family:''Microsoft YaHei'';">年温州贷以</span><span style="font-family:''Microsoft YaHei'';">78.6</span><span style="font-family:''Microsoft YaHei'';">亿元的成交量位居全国</span><span style="font-family:''Microsoft YaHei'';">P2P</span><span style="font-family:''Microsoft YaHei'';">行业之首，交易达</span><span style="font-family:''Microsoft YaHei'';">81991</span><span style="font-family:''Microsoft YaHei'';">笔，超越了平安旗下强势竞争对手陆金所。</span></span><span style="font-family:宋体;font-size:10.5pt;"></span>\r\n</p>\r\n<p class="p0" style="color:#474E5D;font-family:''microsoft yahei'', Tahoma, Helvetica, Arial, sans-serif;font-size:14px;background-color:#FFFFFF;">\r\n	<span style="font-family:''Microsoft YaHei'';"></span>\r\n</p>\r\n<p class="p0" style="color:#474E5D;font-family:''microsoft yahei'', Tahoma, Helvetica, Arial, sans-serif;font-size:14px;background-color:#FFFFFF;">\r\n	<span style="font-family:宋体;font-size:10.5pt;"><span style="font-family:''Microsoft YaHei'';">公司拥有近百余名员工，其中技术团队来自腾讯等知名企业，拥有多年资深行业经验，风控团队来自于知名银行，拥有众多国内外项目背景，为提升温州贷的开发管理水平做出巨大的支持。目前，网站理财投资用户达</span><span style="font-family:''Microsoft YaHei'';">11</span><span style="font-family:''Microsoft YaHei'';">余万人，服务全国中小微企业</span><span style="font-family:''Microsoft YaHei'';">2000</span><span style="font-family:''Microsoft YaHei'';">余家，累计贷款超</span><span style="font-family:''Microsoft YaHei'';">146</span><span style="font-family:''Microsoft YaHei'';">亿元。</span></span><span style="font-family:宋体;font-size:10.5pt;"></span>\r\n</p>\r\n<p class="p0" style="color:#474E5D;font-family:''microsoft yahei'', Tahoma, Helvetica, Arial, sans-serif;font-size:14px;background-color:#FFFFFF;">\r\n	<span style="font-family:''Microsoft YaHei'';"></span>\r\n</p>\r\n<p class="p0" style="color:#474E5D;font-family:''microsoft yahei'', Tahoma, Helvetica, Arial, sans-serif;font-size:14px;background-color:#FFFFFF;">\r\n	<span style="font-family:''Microsoft YaHei'';">温州贷以创造投资人价值为核心，本着诚信、公开、透明的服务理念，为投资者和借款者制定个性化的卓越服务，致力于为广大投资用户提供一个低风险、高收益的理财渠道。注重每一位用户的个人隐私、投资安全以及用户体验，严格遵循国家政策，并从平台的专业性、安全性、功能性着手，深入打造更全面的创新型理财服务。</span>\r\n</p>', '', NULL, '<img src="/data/attached/20141211/20141211113532_87094.png" alt="" />', '<p class="p0" style="color:#474E5D;font-family:''microsoft yahei'', Tahoma, Helvetica, Arial, sans-serif;font-size:14px;background-color:#FFFFFF;">\r\n	<span style="font-family:''Microsoft YaHei'';">联系地址：</span><span style="font-family:''Microsoft YaHei'';">温州市鹿城区车站大道577号财富中心1403</span><span style="font-family:宋体;font-size:10.5pt;"></span>\r\n</p>\r\n<p class="p0" style="color:#474E5D;font-family:''microsoft yahei'', Tahoma, Helvetica, Arial, sans-serif;font-size:14px;background-color:#FFFFFF;">\r\n	<span style="font-family:''Microsoft YaHei'';">联系电话：</span><span style="font-family:''Microsoft YaHei'';">400-0577-985</span><span style="font-family:宋体;font-size:10.5pt;"></span>\r\n</p>\r\n<p class="p0" style="color:#474E5D;font-family:''microsoft yahei'', Tahoma, Helvetica, Arial, sans-serif;font-size:14px;background-color:#FFFFFF;">\r\n	<span style="font-family:''Microsoft YaHei'';">公司电话：0577-89999772</span><span style="font-family:宋体;font-size:10.5pt;"></span>\r\n</p>\r\n<p class="p0" style="color:#474E5D;font-family:''microsoft yahei'', Tahoma, Helvetica, Arial, sans-serif;font-size:14px;background-color:#FFFFFF;">\r\n	<span style="font-family:''Microsoft YaHei'';">公司传真：0577-89999771</span><span style="font-family:宋体;font-size:10.5pt;"></span>\r\n</p>\r\n<p class="p0" style="color:#474E5D;font-family:''microsoft yahei'', Tahoma, Helvetica, Arial, sans-serif;font-size:14px;background-color:#FFFFFF;">\r\n	<span style="font-family:''Microsoft YaHei'';">服务邮箱：admin@wzdai.com</span>\r\n</p>', '<p align="left" style="color:#474E5D;font-family:''microsoft yahei'', Tahoma, Helvetica, Arial, sans-serif;font-size:14px;background:white;">\r\n	<span style="font-family:微软雅黑;">1</span><span style="font-family:微软雅黑;">、温州贷VIP会员信息费150/年（申请VIP成功后扣除）；</span><span style="font-size:12pt;font-family:微软雅黑;"></span>\r\n</p>\r\n<p align="left" style="color:#474E5D;font-family:''microsoft yahei'', Tahoma, Helvetica, Arial, sans-serif;font-size:14px;background:white;">\r\n	<span style="font-family:微软雅黑;"><br />\r\n</span>\r\n</p>\r\n<p align="left" style="color:#474E5D;font-family:''microsoft yahei'', Tahoma, Helvetica, Arial, sans-serif;font-size:14px;background:white;">\r\n	<span style="font-family:微软雅黑;">2</span><span style="font-family:微软雅黑;">、借款者上传资料审核，并通过审核后即可申请VIP会员；</span><span style="font-size:12pt;font-family:微软雅黑;"></span>\r\n</p>\r\n<p align="left" style="color:#474E5D;font-family:''microsoft yahei'', Tahoma, Helvetica, Arial, sans-serif;font-size:14px;background:white;">\r\n	<span style="font-family:微软雅黑;"><br />\r\n</span>\r\n</p>\r\n<p align="left" style="color:#474E5D;font-family:''microsoft yahei'', Tahoma, Helvetica, Arial, sans-serif;font-size:14px;background:white;">\r\n	<span style="font-family:微软雅黑;">3</span><span style="font-family:微软雅黑;">、投资者可自愿申请VIP，成为温州贷会员后,&nbsp;若借款标出现逾期，温州贷将启用风险备用金对用户进行补偿；</span><span style="font-size:12pt;font-family:微软雅黑;"></span>\r\n</p>\r\n<p align="left" style="color:#474E5D;font-family:''microsoft yahei'', Tahoma, Helvetica, Arial, sans-serif;font-size:14px;background:white;">\r\n	<span style="font-family:微软雅黑;"><br />\r\n</span>\r\n</p>\r\n<p align="left" style="color:#474E5D;font-family:''microsoft yahei'', Tahoma, Helvetica, Arial, sans-serif;font-size:14px;background:white;">\r\n	<span style="font-family:微软雅黑;">4</span><span style="font-family:微软雅黑;">、VIP会员期间的逾期信用标享受本金全额补偿，给力标由网站补偿本息，净值标由网站补偿全额本金；</span><span style="font-size:12pt;font-family:微软雅黑;"></span>\r\n</p>\r\n<p align="left" style="color:#474E5D;font-family:''microsoft yahei'', Tahoma, Helvetica, Arial, sans-serif;font-size:14px;background:white;">\r\n	<br />\r\n</p>\r\n<p align="left" style="color:#474E5D;font-family:''microsoft yahei'', Tahoma, Helvetica, Arial, sans-serif;font-size:14px;background-color:#FFFFFF;">\r\n	<span style="font-size:12pt;"></span><span style="font-family:微软雅黑;">5</span><span style="font-family:微软雅黑;">、以下两种情况均可享受VIP会员待遇：</span><span style="font-size:12pt;font-family:微软雅黑;"></span>\r\n</p>\r\n<p align="left" style="color:#474E5D;font-family:''microsoft yahei'', Tahoma, Helvetica, Arial, sans-serif;font-size:14px;background:white;">\r\n	<span style="font-family:微软雅黑;">①在借款标逾期前成为温州贷VIP会员；</span><span style="font-size:9pt;font-family:微软雅黑;"></span>\r\n</p>\r\n<p align="left" style="color:#474E5D;font-family:''microsoft yahei'', Tahoma, Helvetica, Arial, sans-serif;font-size:14px;background-color:#FFFFFF;">\r\n	<span style="font-family:微软雅黑;">②投标时，用户为温州贷VIP会员，但回款前VIP资格期限已到期。</span><span style="font-size:9pt;font-family:微软雅黑;"></span>\r\n</p>\r\n<p align="left" style="color:#474E5D;font-family:''microsoft yahei'', Tahoma, Helvetica, Arial, sans-serif;font-size:14px;background-color:#FFFFFF;">\r\n	<span style="font-family:微软雅黑;"><br />\r\n</span>\r\n</p>\r\n<p align="left" style="color:#474E5D;font-family:''microsoft yahei'', Tahoma, Helvetica, Arial, sans-serif;font-size:14px;background-color:#FFFFFF;">\r\n	<span style="font-family:微软雅黑;">注：<span style="line-height:21px;">非</span><span style="line-height:21px;">VIP</span><span style="line-height:21px;">的普通会员投标给力标和信用标后的逾期补偿为此逾期投标本金的</span><span style="line-height:21px;">50%</span></span>\r\n</p>', 118, '', 0, '刘伟雄', 15767915200, 1418268082),
(47, '<div class="pg-about-section" style="margin:0px;padding:0px;color:#666666;font-family:微软雅黑, YaHei, tahoma, arial, ''Hiragino Sans GB'', 宋体;font-size:14px;">\r\n	<p>\r\n		人人贷(renrendai.com)，系人人友信集团旗下公司及独立品牌。\r\n	</p>\r\n	<p>\r\n		自2010年5月成立至今，人人贷的服务已覆盖了全国30余个省的2000多个地区，服务了几十万名客户，成功帮助他们通过信用申请获得融资借款，或通过自主出借获得稳定收益。\r\n	</p>\r\n	<p>\r\n		作为中国最早的一批基于互联网的P2P信用借贷服务平台，人人贷以其诚信、透明、公平、高效、创新的特征赢得了良好的用户口碑。现在，人人贷已成为行业内最具影响力的品牌之一。\r\n	</p>\r\n</div>\r\n<div class="pg-about-section mt30" style="margin:30px 0px 0px;padding:0px;color:#666666;font-family:微软雅黑, YaHei, tahoma, arial, ''Hiragino Sans GB'', 宋体;font-size:14px;">\r\n	<h2 class="rrdcolor-blue-text" style="font-size:18px;font-weight:500;color:#2EA7E0;">\r\n		使命\r\n	</h2>\r\n	<p>\r\n		实现个人的信用自主和金融自由。\r\n	</p>\r\n</div>\r\n<div class="pg-about-section mt30" style="margin:30px 0px 0px;padding:0px;color:#666666;font-family:微软雅黑, YaHei, tahoma, arial, ''Hiragino Sans GB'', 宋体;font-size:14px;">\r\n	<h2 class="rrdcolor-blue-text" style="font-size:18px;font-weight:500;color:#2EA7E0;">\r\n		愿景\r\n	</h2>\r\n	<p>\r\n		打造一个人人参与、人人自由、人人平等的互联网个人金融服务平台。\r\n	</p>\r\n</div>\r\n<div class="pg-about-section mt30" style="margin:30px 0px 0px;padding:0px;color:#666666;font-family:微软雅黑, YaHei, tahoma, arial, ''Hiragino Sans GB'', 宋体;font-size:14px;">\r\n	<h2 class="rrdcolor-blue-text" style="font-size:18px;font-weight:500;color:#2EA7E0;">\r\n		核心价值\r\n	</h2>\r\n释放用户借款和理财的自主权利借款人通过个人信用申请借款，获得资金；理财人通过公开信息自主选择进行投资，获得收益。建立互联网时代的个人金融风险定价体系通过有效的个人信用模型和大数据挖掘，逐步建立并完善符合时代特征的个人金融风险定价体系。实现更低借款成本和更稳健投资收益受益于精准的个人金融风险定价体系和安全分散有保障的投资机制，借款人和理财人的权益均可得到充分的<br />\r\n保障和满足。\r\n</div>', '', '<p style="color:#666666;font-family:微软雅黑, YaHei, tahoma, arial, ''Hiragino Sans GB'', 宋体;font-size:14px;">\r\n	人人贷以普惠金融的梦想及互联网金融创新的基因凝聚了一批充满阳光、活力和进取精神的金融及技术精英，他们成为了伟大梦想脚踏实地的践行者。\r\n</p>\r\n<ul class="fn-clear" style="color:#666666;font-family:微软雅黑, YaHei, tahoma, arial, ''Hiragino Sans GB'', 宋体;font-size:14px;">\r\n	<li class="team-item rrdcolor-thinblue-text fn-clear mb10" style="background:#F9F9F9;">\r\n		<img width="125px" height="143px" align="middle" src="http://www.renrendai.com/static/img/about/team/zhangshishi.jpg?20140401a" alt="张适时" style="border:1px solid #EEEEEE;" />\r\n		<div class="record" style="margin:0px;padding:0px;">\r\n			<h5 class="rrdcolor-blue-text" style="font-size:18px;font-weight:500;color:#2EA7E0;">\r\n				张适时<span style="color:#0697DA;font-size:14px;">创始合伙人</span>\r\n			</h5>\r\n			<p>\r\n				毕业于清华大学经济管理学院金融系。毕业后背包旅行8个多月，游历包括南极在内的30多个国家和地区。2008年就职于香港Temujin私募基金，后在家族企业富山矿业集团工作，往返新疆内蒙等地。<br />\r\n2010年，与合伙人共同创建互联网信贷服务平台-人人贷。\r\n			</p>\r\n		</div>\r\n	</li>\r\n	<li class="team-item rrdcolor-thinblue-text fn-clear mb10" style="background:#F9F9F9;">\r\n		<img width="125px" height="143px" align="middle" src="http://www.renrendai.com/static/img/about/team/lixinhe.jpg?20140401a" alt="李欣贺" style="border:1px solid #EEEEEE;" />\r\n		<div class="record" style="margin:0px;padding:0px;">\r\n			<h5 class="rrdcolor-blue-text" style="font-size:18px;font-weight:500;color:#2EA7E0;">\r\n				李欣贺<span style="color:#0697DA;font-size:14px;">创始合伙人</span>\r\n			</h5>\r\n			<p>\r\n				毕业于北京大学数学与应用数学学院金融数学系。曾就职于德意志银行直接投资部，曾任中银国际直接投资部经理、渤海产业投资基金投资经理，在金融投资领域有丰富经验。<br />\r\n2010年，与合伙人共同创建互联网信贷服务平台-人人贷。\r\n			</p>\r\n		</div>\r\n	</li>\r\n	<li class="team-item rrdcolor-thinblue-text fn-clear mb10" style="background:#F9F9F9;">\r\n		<img width="125px" height="143px" align="middle" src="http://www.renrendai.com/static/img/about/team/yangyifu.jpg?20140401a" alt="杨一夫" style="border:1px solid #EEEEEE;" />\r\n		<div class="record" style="margin:0px;padding:0px;">\r\n			<h5 class="rrdcolor-blue-text" style="font-size:18px;font-weight:500;color:#2EA7E0;">\r\n				杨一夫<span style="color:#0697DA;font-size:14px;">创始合伙人</span>\r\n			</h5>\r\n			<p>\r\n				毕业于北京大学数学与应用数学学院金融数学系。后赴荷兰学习金融学，并取得金融学硕士学位。归国后将国外先进的个人信用理念和审核技术引进中国，并致力于使用自己在个人信用审核方面的理论知识和实践经验推动中国诚信体系的建设。<br />\r\n2010年，与合伙人共同创建互联网信贷服务平台-人人贷。\r\n			</p>\r\n		</div>\r\n	</li>\r\n	<li class="team-item rrdcolor-thinblue-text fn-clear mb10" style="background:#F9F9F9;">\r\n		<img width="125px" height="143px" align="middle" src="http://www.renrendai.com/static/img/about/team/guchonglun.jpg?20140401a" alt="顾崇伦" style="border:1px solid #EEEEEE;" />\r\n		<div class="record" style="margin:0px;padding:0px;">\r\n			<h5 class="rrdcolor-blue-text" style="font-size:18px;font-weight:500;color:#2EA7E0;">\r\n				顾崇伦<span style="color:#0697DA;font-size:14px;">副总裁</span>\r\n			</h5>\r\n			<p>\r\n				北京大学数学学院计算数学系本科、硕士。曾就职于中国国际金融有限公司（中金）风险管理部，泰达宏利基金管理有限公司产品创新与金融工程部。在风险管理、金融产品创新、量化投资领域均有丰富经验，尤其是海外衍生品和结构化产品的定价与风险控制。取得了"金融风险管理师（FRM）"和"特许金融分析师（CFA Chart Holder）"资格，同时拥有国内证券、基金、期货等从业资格。\r\n			</p>\r\n		</div>\r\n	</li>\r\n	<li class="team-item rrdcolor-thinblue-text fn-clear mb10" style="background:#F9F9F9;">\r\n		<img width="125px" height="143px" align="middle" src="http://www.renrendai.com/static/img/about/team/fanxiaolei.jpg?20140401a" alt="范晓磊" style="border:1px solid #EEEEEE;" />\r\n		<div class="record" style="margin:0px;padding:0px;">\r\n			<h5 class="rrdcolor-blue-text" style="font-size:18px;font-weight:500;color:#2EA7E0;">\r\n				范晓磊<span style="color:#0697DA;font-size:14px;">风险管理部总监</span>\r\n			</h5>\r\n			<p>\r\n				毕业于清华大学经济管理学院金融学专业。毕业后先后就职于法国外贸银行股份有限公司（NATIXIS）和中国进出口银行，任交易员，从事利率汇率交易业务，有丰富的市场风险管理经验。了解国内外债务市场，对信用风险评估和定价机制有深入研究，熟悉银行业务和产品体系。取得"特许金融分析师（CFA Chart Holder）"资格。\r\n			</p>\r\n		</div>\r\n	</li>\r\n	<li class="team-item rrdcolor-thinblue-text fn-clear mb10" style="background:#F9F9F9;">\r\n		<img width="125px" height="143px" align="middle" src="http://www.renrendai.com/static/img/about/team/lanyanxiang.jpg?20140401a" alt="蓝晏翔" style="border:1px solid #EEEEEE;" />\r\n		<div class="record" style="margin:0px;padding:0px;">\r\n			<h5 class="rrdcolor-blue-text" style="font-size:18px;font-weight:500;color:#2EA7E0;">\r\n				蓝晏翔<span style="color:#0697DA;font-size:14px;">技术总监</span>\r\n			</h5>\r\n			<p>\r\n				毕业于清华大学计算机系本科。毕业后进入“清华-微软”联合培养博士生项目获得博士学位。长期在微软亚洲研究院参与下一代图形图像系统、设备以及体感交互设备的研究和开发。对于计算理论、优化算法有深刻理解。\r\n			</p>\r\n		</div>\r\n	</li>\r\n	<li class="team-item rrdcolor-thinblue-text fn-clear mb10" style="background:#F9F9F9;">\r\n		<img width="125px" height="143px" align="middle" src="http://www.renrendai.com/static/img/about/team/bijian.jpg?20140401a" alt="毕建" style="border:1px solid #EEEEEE;" />\r\n		<div class="record" style="margin:0px;padding:0px;">\r\n			<h5 class="rrdcolor-blue-text" style="font-size:18px;font-weight:500;color:#2EA7E0;">\r\n				毕建<span style="color:#0697DA;font-size:14px;">市场总监</span>\r\n			</h5>\r\n			<p>\r\n				毕业于东北大学经贸系，后赴英国南安普顿大学学习国际金融专业，04年归国后加入宝洁中国，先后担任现代渠道区域总监和Olay品牌全国市场营销总监等职务，主要负责制定品牌发展战略、品牌传播策略和渠道营销策略等工作。在渠道整合、用户研究和品牌营销方面具有丰富经验。\r\n			</p>\r\n		</div>\r\n	</li>\r\n	<li class="team-item rrdcolor-thinblue-text fn-clear mb10" style="background:#F9F9F9;">\r\n		<img width="125px" height="143px" align="middle" src="http://www.renrendai.com/static/img/about/team/hanjiaming.jpg?20140401a" alt="韩家铭" style="border:1px solid #EEEEEE;" />\r\n		<div class="record" style="margin:0px;padding:0px;">\r\n			<h5 class="rrdcolor-blue-text" style="font-size:18px;font-weight:500;color:#2EA7E0;">\r\n				韩家铭<span style="color:#0697DA;font-size:14px;">财务总监</span>\r\n			</h5>\r\n			<p>\r\n				毕业于中国人民大学商学院会计系财务管理专业，毕业后加入德勤华永会计师事务所工作八年，担任审计经理，期间参与多家知名国有及民营企业上市及年度审计。后在卓展集团控股有限公司担任总会计师，对公司财务预算、分析、税务和资金管理均有深入的理解。拥有中国注册会计师资格，对中国会计准则和国际会计准则均有研究。\r\n			</p>\r\n		</div>\r\n	</li>\r\n	<li class="team-item rrdcolor-thinblue-text fn-clear mb10" style="background:#F9F9F9;">\r\n		<img width="125px" height="143px" align="middle" src="http://www.renrendai.com/static/img/about/team/wangjian.jpg?20140401a" alt="王坚" style="border:1px solid #EEEEEE;" />\r\n		<div class="record" style="margin:0px;padding:0px;">\r\n			<h5 class="rrdcolor-blue-text" style="font-size:18px;font-weight:500;color:#2EA7E0;">\r\n				王坚<span style="color:#0697DA;font-size:14px;">战略合作部总监</span>\r\n			</h5>\r\n			<p>\r\n				毕业于清华大学经济管理学院金融系，后赴纽约大学学习，并取得经济学硕士学位。在香港先后就职于高盛亚洲、中国国际金融有限公司，从事股票和股指期货交易，精通场外大宗交易、指数调整调仓交易、ETF及股指期货套利，熟悉亚洲主要资本市场，在商业分析和金融投资交易方面有丰富经验。\r\n			</p>\r\n		</div>\r\n	</li>\r\n</ul>', NULL, '<div class="pg-about-section" style="margin:0px;padding:0px;">\r\n	<p class="line30">\r\n		人人贷当前的服务已覆盖了全国30余个省的2000多个地区，在北京的西直门和五道口均设有办公场所，在上海设有专业的信审服务中心。\r\n	</p>\r\n</div>\r\n<div class="pg-about-section mt20" style="margin:20px 0px 0px;padding:0px;">\r\n	<h2 class="rrdcolor-blue-text" style="font-size:18px;font-weight:500;color:#2EA7E0;">\r\n		北京总部\r\n	</h2>\r\n	<ul class="li-height30">\r\n		<li>\r\n			地址：北京市海淀区西直门外高梁桥斜街59号中坤大厦7层\r\n		</li>\r\n		<li>\r\n			邮编：100044\r\n		</li>\r\n	</ul>\r\n</div>\r\n<div class="pg-about-section mt20" style="margin:20px 0px 0px;padding:0px;">\r\n	<h2 class="rrdcolor-blue-text" style="font-size:18px;font-weight:500;color:#2EA7E0;">\r\n		北京分部\r\n	</h2>\r\n	<ul class="li-height30">\r\n		<li>\r\n			地址：北京市海淀区五道口优盛大厦18层\r\n		</li>\r\n		<li>\r\n			邮编：100083\r\n		</li>\r\n	</ul>\r\n</div>\r\n<div class="pg-about-section mt20" style="margin:20px 0px 0px;padding:0px;">\r\n	<h2 class="rrdcolor-blue-text" style="font-size:18px;font-weight:500;color:#2EA7E0;">\r\n		客服电话\r\n	</h2>\r\n	<ul class="li-height30">\r\n		<li>\r\n			如果您在使用人人贷(renrendai.com)的过程中有任何疑问请您与人人贷客服人员联系。\r\n		</li>\r\n		<li>\r\n			客服电话：400-090-6600\r\n		</li>\r\n		<li>\r\n			在线帮助：<a href="http://www.renrendai.com/help/index.action" target="_blank">www.renrendai.com/help/index.action</a>\r\n		</li>\r\n	</ul>\r\n</div>\r\n<div class="pg-about-section mt20" style="margin:20px 0px 0px;padding:0px;">\r\n	<h2 class="rrdcolor-blue-text" style="font-size:18px;font-weight:500;color:#2EA7E0;">\r\n		媒体采访\r\n	</h2>\r\n	<p class="line30">\r\n		如果有媒体采访需求，请将您的媒体名称、采访提纲、联系方式发至：<br />\r\n<a href="mailto:PR@renrendai.com" target="_blank">PR@renrendai.com</a>，我们会尽快与您联系。\r\n	</p>\r\n</div>\r\n<div class="pg-about-section mt20" style="margin:20px 0px 0px;padding:0px;">\r\n	<h2 class="rrdcolor-blue-text" style="font-size:18px;font-weight:500;color:#2EA7E0;">\r\n		商务合作\r\n	</h2>\r\n	<p class="line30">\r\n		如果贵公司希望与我们建立商务合作关系，形成优势互补，请将合作意向进行简要描述并发送邮件至：<br />\r\n<a href="mailto:BD@renrendai.com" target="_blank">BD@renrendai.com</a>，我们会尽快与您联系。&nbsp;<br />\r\n备注：人人贷目前暂时没有线下加盟/线下开店等商务合作模式，谢谢关注。\r\n	</p>\r\n</div>\r\n<div class="pg-about-section information-con mt20" style="margin:20px 0px 0px;padding:0px;">\r\n	<h2 class="rrdcolor-blue-text" style="font-size:18px;font-weight:500;color:#2EA7E0;">\r\n		信息同步\r\n	</h2>\r\n	<p class="line30">\r\n		如果您还希望了解我们更多信息，请关注我们的新浪微博、腾讯微博、微信或博客。\r\n	</p>\r\n	<div class="con-way" style="margin:0px;padding:0px;">\r\n		<ul class="li-height30" style="color:#666666;font-family:微软雅黑, YaHei, tahoma, arial, ''Hiragino Sans GB'', 宋体;font-size:14px;background-color:#FFFFFF;">\r\n			<li>\r\n				微信公众平台：renrendai（扫描右侧二维码关注人人贷微信）\r\n			</li>\r\n			<li>\r\n				新浪博客：<a href="http://blog.sina.com.cn/renrendai" target="_blank">blog.sina.com.cn/renrendai</a>\r\n			</li>\r\n			<li>\r\n				新浪微博：<a href="http://e.weibo.com/renrendai" target="_blank">e.weibo.com/renrendai</a>\r\n			</li>\r\n			<li>\r\n				腾讯微博：<a href="http://t.qq.com/renrendai" target="_blank">t.qq.com/renrendai</a>\r\n			</li>\r\n			<li>\r\n			</li>\r\n		</ul>\r\n		<div class="weixin" id="qr-code-weixin" style="margin:0px;padding:0px;color:#666666;font-family:微软雅黑, YaHei, tahoma, arial, ''Hiragino Sans GB'', 宋体;font-size:14px;background-color:#FFFFFF;">\r\n		</div>\r\n	</div>\r\n</div>', NULL, 119, '', 0, '老刘', 15767915200, 1418268154),
(48, '<p style="font-size:14px;color:#444444;font-family:寰蒋闆呴粦, Helvetica, Arial, sans-serif;">\r\n	<span><img id="asdfg" alt="" src="https://www.bank166.com/public/kindeditor/attached/image/20130930/20130930082748_56334.jpg" /></span>&nbsp;\r\n</p>\r\n<p style="font-size:14px;color:#444444;font-family:寰蒋闆呴粦, Helvetica, Arial, sans-serif;">\r\n	<span style="font-family:''Microsoft YaHei'';line-height:1.5;">&nbsp; &nbsp; &nbsp; &nbsp;财富天下网站隶属于深圳市钻石财富金融集团有限公司（<span>以下简称“钻石集团”</span>）。<span>钻石集团</span><span>经深圳市市场监督管理局批准，</span>于<span>2011年5月18日</span>正式成立，旗下拥有钻石财富基金管理有限公司、财富天下金融服务有限公司、汇金消费金融服务有限公司、利广拍卖有限公司、财富天下证券投资咨询有限公司、钻石财富管理中心、中部商品交易市场（平江）及分布在广州、东莞、湖南长沙、江西南昌、福建福州的分公司等共20多家子公司，联合注册资本20多亿元人民币。</span>\r\n</p>\r\n<p style="font-size:14px;color:#444444;font-family:寰蒋闆呴粦, Helvetica, Arial, sans-serif;">\r\n	<strong><span style="font-family:''Microsoft YaHei'';">一、许可经营项目</span></strong><span style="font-family:''Microsoft YaHei'';"> </span>\r\n</p>\r\n<p style="font-size:14px;color:#444444;font-family:寰蒋闆呴粦, Helvetica, Arial, sans-serif;">\r\n	<span style="font-family:''Microsoft YaHei'';">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 经济信息咨询、企业管理咨询、投资咨询、信息咨询；从事担保业务；劳务派遣；受托资产管理；电子商务；投资管理；金融软件技术开发；转让自行开发的技术成果、技术咨询；受金融机构委托对信贷逾期户及信用卡透支户进行电话通知服务。</span>\r\n</p>\r\n<p style="font-size:14px;color:#444444;font-family:寰蒋闆呴粦, Helvetica, Arial, sans-serif;">\r\n	<strong><span style="font-family:''Microsoft YaHei'';">二、网络理财与投融资平台</span></strong>\r\n</p>\r\n<p style="font-size:14px;color:#444444;font-family:寰蒋闆呴粦, Helvetica, Arial, sans-serif;">\r\n	<span style="font-family:''Microsoft YaHei'';">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 财富天下结合全球金融发展与互联网技术创新，致力于为中小微企业提供融资新渠道，为个人提供创新型投资理财服务。在健全的风险管控体系基础上，打造一站式服务的金融超市，资源整合的金融平台，最优、最省、最快、最专业的金融保姆，线上线下并行、实体虚拟并行为中小企业及个人客户提供专业、可信赖的投融资服务，实现财富增值。</span>\r\n</p>\r\n<p class="p0" style="font-size:14px;color:#444444;font-family:寰蒋闆呴粦, Helvetica, Arial, sans-serif;text-indent:17.64pt;">\r\n	<span style="font-family:''Microsoft YaHei'';font-weight:bold;">三、组织架构</span><span style="font-family:宋体;font-size:9pt;font-weight:bold;"></span>\r\n</p>\r\n<p style="font-size:14px;color:#444444;font-family:寰蒋闆呴粦, Helvetica, Arial, sans-serif;">\r\n	<span style="font-family:''Microsoft YaHei'';">&nbsp;&nbsp;&nbsp;&nbsp; 根据经营管理需要，财富天下现内设融资部、项目部、运营部，其中运营部下设企划部、技术部以及由客服部、财务部、风控部、产品部组成的平台支持。</span>\r\n</p>\r\n<p class="p0" style="font-size:14px;color:#444444;font-family:寰蒋闆呴粦, Helvetica, Arial, sans-serif;text-indent:17.64pt;">\r\n	<span style="font-family:''Microsoft YaHei'';font-weight:bold;">四、运营团队</span><span style="font-family:宋体;font-size:9pt;font-weight:bold;"></span>\r\n</p>\r\n<p class="p0" style="font-size:14px;color:#444444;font-family:寰蒋闆呴粦, Helvetica, Arial, sans-serif;text-indent:18pt;">\r\n	<span style="font-family:''Microsoft YaHei'';">财富天下运营团队由金融管理和电子商务等业界一流的专业人士组成，结合丰富的综合金融的实践经验，拥有行业领先的风险管控能力与先进、安全的互联网平台。</span>\r\n</p>\r\n<p class="p0" style="font-size:14px;color:#444444;font-family:寰蒋闆呴粦, Helvetica, Arial, sans-serif;text-indent:18pt;">\r\n	<img alt="" src="https://www.bank166.com/public/kindeditor/attached/image/20130930/20130930085010_22875.jpg" /><br />\r\n<span style="font-family:宋体;font-size:9pt;"></span><span style="font-family:宋体;font-size:10.5pt;"></span>\r\n</p>\r\n<p class="p0" style="font-size:14px;color:#444444;font-family:寰蒋闆呴粦, Helvetica, Arial, sans-serif;text-indent:18pt;">\r\n	<span style="font-family:''Microsoft YaHei'';font-weight:bold;">五、联系我们</span><span style="font-family:宋体;font-size:9pt;font-weight:bold;"></span>\r\n</p>\r\n<p class="p0" style="font-size:14px;color:#444444;font-family:寰蒋闆呴粦, Helvetica, Arial, sans-serif;">\r\n	<span style="font-family:宋体;font-size:9pt;"><span style="font-family:''Microsoft YaHei'';font-size:14px;"><span style="line-height:21px;">&nbsp; &nbsp; &nbsp; 深圳市钻石财富金融集团有限公司</span>位于深圳市中心福田区，</span><span style="font-family:''Microsoft YaHei'';font-size:14px;">办公面积</span><span style="font-family:''Microsoft YaHei'';font-size:14px;">1700</span><span style="font-family:''Microsoft YaHei'';font-size:14px;">多平米，地理位置优越，实力雄厚。</span></span><span style="font-family:宋体;font-size:9pt;"></span>&nbsp;<br />\r\n<span style="font-family:宋体;font-size:9pt;"><span style="font-family:''Microsoft YaHei'';font-size:14px;">&nbsp; &nbsp; &nbsp;联系电话：</span><span style="font-family:''Microsoft YaHei'';font-size:14px;">0755-22289739、22289738、22289798、22289729、22289728、22289718 &nbsp;&nbsp;&nbsp;&nbsp;</span></span><span style="font-family:宋体;font-size:9pt;"></span><br />\r\n<span style="font-family:宋体;font-size:9pt;"><span style="font-family:''Microsoft YaHei'';font-size:14px;">&nbsp; &nbsp; &nbsp;传真：0755-</span><span style="font-family:''Microsoft YaHei'';font-size:14px;"><span style="font-family:宋体;font-size:9pt;"><span style="font-family:''Microsoft YaHei'';font-size:14px;">22289729</span></span></span></span><br />\r\n<span style="font-family:宋体;font-size:9pt;"><span style="font-family:''Microsoft YaHei'';font-size:14px;">&nbsp; &nbsp; &nbsp;全国免费客服热线：</span><span style="font-family:''Microsoft YaHei'';font-size:14px;">400-8250-168</span></span><span style="font-family:宋体;font-size:9pt;"></span>&nbsp;<br />\r\n<span style="font-family:宋体;font-size:9pt;"><span style="font-family:''Microsoft YaHei'';font-size:14px;">&nbsp; &nbsp; &nbsp;公司地址： 广东省深圳市福田中心区益田路4068号卓越时代广场46楼</span></span>\r\n</p>\r\n<p class="p0" style="font-size:14px;color:#444444;font-family:寰蒋闆呴粦, Helvetica, Arial, sans-serif;">\r\n	<span style="font-family:宋体;font-size:9pt;"><span style="font-family:''Microsoft YaHei'';font-size:14px;"><img src="https://www.bank166.com/public/kindeditor/attached/image/20140403/20140403120752_39852.jpg" alt="" /></span></span>\r\n</p>', '', NULL, NULL, '<p class="MsoNormal" style="font-size:14px;color:#444444;font-family:寰蒋闆呴粦, Helvetica, Arial, sans-serif;">\r\n	<strong>、联系我们</strong>\r\n</p>\r\n<p class="MsoNormal" style="font-size:14px;color:#444444;font-family:寰蒋闆呴粦, Helvetica, Arial, sans-serif;">\r\n	<strong></strong>\r\n</p>\r\n<div style="margin:0px;padding:0px;font-family:寰蒋闆呴粦, Helvetica, Arial, sans-serif;">\r\n	<strong>深圳市钻石财富金融集团有限公司<strong>位于深圳市中心福田区，办公面积1700多平米，地理位置优越，实力雄厚。</strong></strong>\r\n</div>\r\n<strong></strong><span style="font-family:寰蒋闆呴粦, Helvetica, Arial, sans-serif;line-height:35px;"></span>\r\n<p class="MsoNormal" style="font-size:14px;color:#444444;font-family:寰蒋闆呴粦, Helvetica, Arial, sans-serif;">\r\n	<strong>联系电话：0755-22289739、22289738、22289798、22289729、22289728、22289718&nbsp; &nbsp;&nbsp;&nbsp;<span class="apple-converted-space">&nbsp;</span></strong>\r\n</p>\r\n<p class="MsoNormal" style="font-size:14px;color:#444444;font-family:寰蒋闆呴粦, Helvetica, Arial, sans-serif;">\r\n	<strong>传真：0755-22289729</strong>\r\n</p>\r\n<p class="MsoNormal" style="font-size:14px;color:#444444;font-family:寰蒋闆呴粦, Helvetica, Arial, sans-serif;">\r\n	<strong>全国免费客服热线：400-8250-168</strong>\r\n</p>\r\n<p class="MsoNormal" style="font-size:14px;color:#444444;font-family:寰蒋闆呴粦, Helvetica, Arial, sans-serif;">\r\n	<strong>公司地址：&nbsp;&nbsp;深圳市福田中心区益田路4068号卓越时代广场46楼</strong>\r\n</p>\r\n<p class="MsoNormal" style="font-size:14px;color:#444444;font-family:寰蒋闆呴粦, Helvetica, Arial, sans-serif;">\r\n	<strong><br />\r\n</strong>\r\n</p>\r\n<p style="font-size:14px;color:#444444;font-family:寰蒋闆呴粦, Helvetica, Arial, sans-serif;">\r\n	<img src="https://www.bank166.com/public/kindeditor/attached/image/20140721/20140721155912_92441.jpg" alt="" width="600px;" />\r\n</p>', NULL, 120, '', 0, '老刘', 15767915200, 1418269823),
(49, '<h4 style="font-family:''Microsoft Yahei'', Arial, ''Helvetica 微软雅黑'';color:#515151;font-size:14px;font-weight:normal;">\r\n	<p class="MsoNormal" align="left" style="text-indent:22.7pt;">\r\n		<span style="font-family:'''';">深圳市合拍在线互联网金融服务有限公司（以下简称合拍在线）是<span>P2P</span>网络借贷理财行业的领军企业，成立于<span>2012</span>年<span>5</span>月，注册资本金<span>1</span>亿元人民币，总部位于改革开放前沿深圳<span>CBD</span>中心区。<a name="_GoBack"></a><span></span></span>\r\n	</p>\r\n	<p class="MsoNormal" align="left" style="text-indent:22.7pt;">\r\n		<span style="font-family:'''';">作为一家严格遵照国家相关法律法规、定位于信息中介服务的互联网金融服务平台，合拍在线旨在为中小微企业提供成本合理以及快捷的融资新渠道，为广大投资人提供高收益、低风险、资金流转便捷的创新型线上投资理财服务，致力于为投资人打造最安全的网络借贷理财平台。<span></span></span>\r\n	</p>\r\n	<p class="MsoNormal" align="left" style="text-indent:22.7pt;">\r\n		<span style="font-family:'''';">依托个性化的线上产品设计、线下合作担保机构分布全国的网点，合拍在线以创新的<span>O2O</span>线上线下结合模式、严谨稳健的风险控制体系、良好的用户口碑，晋升全国<span>P2P</span>网贷平台前列。<span>2</span>年半以来，随着品牌知名度的提升和规模效益的显现，合拍在线年度交易额以<span>200%</span>的惊人速度爆炸式增长。<span>2014</span>年以来，合拍在线已多次排名累计交易额前三甲（数据来源：网贷之家），并以<span>3.89%</span>市场份额获得<span>2014</span>年第二季度<span>P2P</span>网贷平台市场份额首位（数据来源：易观智库）。 截止<span>2014</span>年<span>11</span>月，合拍在线累积成交额突破<span>75</span>亿，为投资人带来收益超<span>9500</span>万，从未有任何一位投资人在合拍资金受到损失！<span></span></span>\r\n	</p>\r\n	<p class="MsoNormal" align="left" style="text-indent:22.7pt;">\r\n		<span style="font-family:'''';">2013</span><span style="font-family:'''';">年<span>11</span>月，作为广东区域代表性互联网金融企业，合拍在线接受了来自于央行、财政部、银监会、证监会、财政部、国务院法制办等八部委的联合现场调研，前期取得的市场成就及运作模式获得监管当局的高度认可。荣获“中国产学研合作创新示范企业”、“<span>CCTV</span>中国影响力品牌企业”、“中国网络借贷金融十大影响力品牌”等诸多荣誉。<span></span></span>\r\n	</p>\r\n	<p class="MsoNormal" align="left" style="text-indent:22.7pt;">\r\n		<span style="font-family:'''';">未来，合拍在线将持续致力于解决中小微企业融资难题，扶持更多的中小微企业实现企业的腾飞发展，通过建立安全、高效、诚信、互助互惠的全网金融模式，助力中国产业链升级！</span><span style="font-family:'''';"></span>\r\n	</p>\r\n	<div>\r\n		<span style="font-family:'''';"><br />\r\n</span>\r\n	</div>\r\n</h4>', '', '<h4 style="font-family:''Microsoft Yahei'', Arial, ''Helvetica 微软雅黑'';color:#515151;font-size:14px;font-weight:normal;">\r\n	<img src="http://www.he-pai.cn/member/upload/pms/201410/31155451r17o.jpg" alt="" />\r\n	<div id="1" style="margin:0px;padding:0px;">\r\n		<img src="http://www.he-pai.cn/member/upload/pms/201410/31160121bixj.jpg" alt="" />\r\n	</div>\r\n<br />\r\n<br />\r\n	<div id="2" style="margin:0px;padding:0px;">\r\n		<img src="http://www.he-pai.cn/member/upload/pms/201410/31160133bgre.jpg" alt="" />\r\n	</div>\r\n<br />\r\n<br />\r\n	<div id="3" style="margin:0px;padding:0px;">\r\n		<img src="http://www.he-pai.cn/member/upload/pms/201410/31160140e74m.jpg" alt="" />\r\n	</div>\r\n<br />\r\n<br />\r\n	<div id="4" style="margin:0px;padding:0px;">\r\n		<img src="http://www.he-pai.cn/member/upload/pms/201411/261615347n5h.jpg" alt="" />\r\n	</div>\r\n</h4>', '<h4 style="font-family:''Microsoft Yahei'', Arial, ''Helvetica 微软雅黑'';color:#515151;font-size:14px;font-weight:normal;">\r\n	<p style="text-indent:25px;">\r\n		<strong><span style="color:#808080;font-size:16px;">合拍在线营业执照</span></strong> \r\n	</p>\r\n	<p style="text-indent:25px;">\r\n		<img src="http://www.he-pai.cn/member/upload/pms/201411/281121374w5i.jpg" alt="" /> \r\n	</p>\r\n	<p style="text-indent:25px;">\r\n		<strong><span style="color:#808080;font-size:16px;">合拍在线组织机构代码证 </span></strong> \r\n	</p>\r\n	<p style="text-indent:25px;">\r\n		<strong><span style="color:#808080;font-size:16px;"><img src="http://www.he-pai.cn/member/upload/pms/201411/28112102uahe.jpg" alt="" /></span></strong> \r\n	</p>\r\n	<p style="text-indent:25px;">\r\n		<br />\r\n	</p>\r\n	<p style="text-indent:25px;">\r\n		<strong><span style="color:#808080;font-size:16px;"><br />\r\n</span></strong> \r\n	</p>\r\n	<p style="text-indent:25px;">\r\n		<strong><span style="color:#808080;font-size:16px;">资质</span></strong> \r\n	</p>\r\n<br />\r\n2012.05 通过中国电子商务协会可信电子商务验证。 <br />\r\n<img width="120" height="50" src="http://www.he-pai.cn/member/upload/pms/201410/clip_image004.jpg" /> <br />\r\n<br />\r\n2013.08 获得Norton Secured Seal 最高级别安全认证。 <br />\r\n<img width="120" height="50" src="http://www.he-pai.cn/member/upload/pms/201410/clip_image006.jpg" /> <br />\r\n<br />\r\n2013.08 成为上海市信息服务业行业协会会员单位。 <br />\r\n<img width="320" height="400" src="http://www.he-pai.cn/member/upload/pms/201410/clip_image007.jpg" alt="1.jpg" /> <br />\r\n<br />\r\n2013.09 成为广东省小商品协会理事单位。 <br />\r\n<img width="320" height="400" src="http://www.he-pai.cn/member/upload/pms/201410/clip_image008.jpg" alt="2.jpg" /> <br />\r\n<br />\r\n2013.12 通过深圳市公安局公共信息安全网络监察备案。 <br />\r\n<img width="120" height="50" src="http://www.he-pai.cn/member/upload/pms/201410/clip_image009.jpg" /> <br />\r\n<br />\r\n2013.12 通过深圳市市场监督管理局网络经营者认证。 <br />\r\n<img width="120" height="50" src="http://www.he-pai.cn/member/upload/pms/201410/clip_image010.jpg" /> <br />\r\n<br />\r\n2014.03 通过“企业信用评价A级认证”（中国互联网协会）。 <br />\r\n<img width="320" height="230" src="http://www.he-pai.cn/member/upload/pms/201410/clip_image011.jpg" alt="3.jpg" /> <br />\r\n<br />\r\n2014.03 成为中国小额信贷联盟会员。 <br />\r\n<img width="320" height="200" src="http://www.he-pai.cn/member/upload/pms/201410/clip_image012.jpg" alt="4.jpg" /> <br />\r\n<br />\r\n2014.03 成为中国小额信贷联盟 P2P 小额信贷信息咨询服务机构行业委员会会员。 <br />\r\n	<p style="text-indent:25px;">\r\n		<img width="320" height="406" src="http://www.he-pai.cn/member/upload/pms/201410/clip_image013.jpg" alt="5.jpg" /> \r\n	</p>\r\n	<p class="MsoNormal" style="text-indent:25px;">\r\n		2014.10 通过安全联盟可信任网站验证\r\n	</p>\r\n	<p class="MsoNormal" style="text-indent:25px;">\r\n		<img src="http://www.he-pai.cn/member/upload/pms/201412/01162624g5a7.jpg" alt="" /> \r\n	</p>\r\n	<p class="MsoNormal" style="text-indent:25px;">\r\n		2014.11 成为中国互联网金融行业协会会员单位<span></span> \r\n	</p>\r\n	<p style="text-indent:25px;">\r\n		<img src="http://www.he-pai.cn/member/upload/pms/201411/28111000pm09.jpg" alt="" /> \r\n	</p>\r\n	<p style="text-indent:25px;">\r\n		<br />\r\n	</p>\r\n<strong><span style="font-size:16px;color:#808080;">荣誉 </span></strong><br />\r\n<br />\r\n2013.05 “中国网络借贷金融服务十大影响力品牌”（中国企业诚信与竞争力论坛活动组委会）。 <br />\r\n<img width="320" height="230" src="http://www.he-pai.cn/member/upload/pms/201410/clip_image014.jpg" alt="6.jpg" /> <br />\r\n<br />\r\n2013.11“最佳金融创新产品奖”（第七届中国（深圳）国际金融博览会组委会）。<br />\r\n<br />\r\n<img width="320" height="220" src="http://www.he-pai.cn/member/upload/pms/201410/clip_image015.jpg" alt="7.jpg" /> <br />\r\n<br />\r\n2014.01 “CCTV中国影响力品牌企业”（CCTV中国影响力栏目组）。 <br />\r\n<img width="320" height="230" src="http://www.he-pai.cn/member/upload/pms/201410/clip_image017.jpg" alt="9.jpg" /> <br />\r\n<br />\r\n2014.03 “2013 年中国互联网金融新锐企业50强”（国培机构、搜狐、商界非常道）。 <br />\r\n<img width="320" height="228" src="http://www.he-pai.cn/member/upload/pms/201410/clip_image018.jpg" alt="10.jpg" /> <br />\r\n<br />\r\n2014.05 “中国电子商务诚信经营示范创建单位”（中国互联网信用评价中心）。 <img width="320" height="230" src="http://www.he-pai.cn/member/upload/pms/201410/clip_image020.jpg" /> <br />\r\n<br />\r\n2014.06 “中国自主品牌成长100强”（中国品牌传播联盟）。 <br />\r\n<a name="undefined"></a><img width="320" height="400" src="http://www.he-pai.cn/member/upload/pms/201410/clip_image022.jpg" /> <br />\r\n<br />\r\n<br />\r\n2014.06 “2014最具成长性企业”（2014（第三届）中国财峰会组委会）<br />\r\n<br />\r\n<img width="320" height="230" src="http://www.he-pai.cn/member/upload/pms/201410/clip_image025.jpg" /><br />\r\n<br />\r\n	<p style="text-indent:25px;">\r\n		<img width="320" height="450" src="http://www.he-pai.cn/member/upload/pms/201410/clip_image024.jpg" /> \r\n	</p>\r\n	<p style="text-indent:25px;">\r\n		<br />\r\n	</p>\r\n	<p class="MsoNormal" style="text-indent:25px;">\r\n		2014.11 “国际互联网金融诚信品牌”（国际品牌协会）<span></span> \r\n	</p>\r\n	<p style="text-indent:25px;">\r\n		<img src="http://www.he-pai.cn/member/upload/pms/201411/28111056ueli.jpg" alt="" /> \r\n	</p>\r\n	<p style="text-indent:25px;">\r\n		<img src="http://www.he-pai.cn/member/upload/pms/201411/28111207q6a7.jpg" alt="" /> \r\n	</p>\r\n	<p style="text-indent:25px;">\r\n		<br />\r\n	</p>\r\n	<p style="text-indent:25px;">\r\n		<span>2014.11 中国产学研合作创新示范企业（中国产学研促进合作会）</span> \r\n	</p>\r\n	<p style="text-indent:25px;">\r\n		<img src="http://www.he-pai.cn/member/upload/pms/201411/28111255fz6k.jpg" alt="" /> \r\n	</p>\r\n</h4>', '<div class="qrcode" style="margin:25px 0px 0px;padding:16px 0px 16px 35px;font-family:''Microsoft Yahei'', Arial, ''Helvetica 微软雅黑'';color:#515151;text-align:center;background-color:#434A54;">\r\n	<p style="color:#878F9B;font-size:14px;text-align:left;">\r\n		扫描微信二维码<br />\r\n随时了解合拍最`新信息<br />\r\n微信号：hepaizaixian<br />\r\n电话：0755-83170302\r\n	</p>\r\n</div>\r\n<div class="tel" style="margin:0px;padding:40px 0px 0px;font-family:''Microsoft Yahei'', Arial, ''Helvetica 微软雅黑'';font-size:14px;color:#878F9B;text-align:center;background-color:#434A54;">\r\n	<span style="font-size:24px;color:#F8B61A;line-height:32px;background:url(http://www.he-pai.cn/skin/pms/default/img/dot02.gif) 20px 50% no-repeat;">400-666-8585<br />\r\n400-012-2210</span>深圳市福田区滨河路5022号联合广场B座7楼\r\n</div>', NULL, 122, '', 0, '刘伟雄', 15767915200, 1418270470);
INSERT INTO `k_company_info` (`cpid`, `info`, `mainbissness`, `team`, `voucher`, `contact_us`, `charge`, `uid`, `boss_name`, `boss_mobile`, `operation_name`, `operation_mobile`, `dateline`) VALUES
(50, '<p>\r\n	<span style="color:#333333;font-family:tahoma, arial, sans-serif;background-color:#FFFFFF;">鼎信贷是由温州众人通民间融资信息服务有限公司打造的融资需求与理财需求提供服务的互联网金融信息服务平台，并对鼎信贷平台产生的经营风险承担法律责任。</span>\r\n</p>\r\n<p>\r\n	<span style="color:#333333;font-family:tahoma, arial, sans-serif;background-color:#FFFFFF;"><span style="color:#333333;font-family:tahoma, arial, sans-serif;background-color:#FFFFFF;">温州众人通民间融资信息服务有限公司位于中国民营经济高度发达和改革开放先驱地的浙江温州，鼎信贷立足浙江温州发达的经济基础，积极响应温州市金融综合改革试验区的政策，以推动温州民间金融发展的公开化、阳光化而努力，鼎信贷以鼎信、公平、专业、透明的理念为融资方和理财方搭建一个方便、安全、轻松、透明的民间融资信息服务平台。</span></span>\r\n</p>\r\n<p>\r\n	<span style="color:#333333;font-family:tahoma, arial, sans-serif;background-color:#FFFFFF;"><span style="color:#333333;font-family:tahoma, arial, sans-serif;background-color:#FFFFFF;">鼎信贷平台由从业于金融、互联网行业多年的精英团队倾力打造，自2012年12月24日上线运营以来，凭借公司股东多年的民间投融资经验结合专业管理团队的不懈努力，服务了众多的鼎信贷客户，成功帮助他们通过申请获得融资借款，或通过自主理财获得稳定收益。</span><br />\r\n<span style="color:#333333;font-family:tahoma, arial, sans-serif;background-color:#FFFFFF;">2014年4月我们成为温州首家获批民间融资信息服务经营资格的网络金融借贷信息服务平台。</span></span>\r\n</p>\r\n<p>\r\n	<span style="color:#333333;font-family:tahoma, arial, sans-serif;background-color:#FFFFFF;"><span style="color:#333333;font-family:tahoma, arial, sans-serif;background-color:#FFFFFF;">鼎信：取信于天下，纳财于鼎上！&nbsp;</span><br />\r\n<span style="color:#333333;font-family:tahoma, arial, sans-serif;background-color:#FFFFFF;">公平：众人通的鼎信贷，就是众人的鼎信贷！</span><br />\r\n<span style="color:#333333;font-family:tahoma, arial, sans-serif;background-color:#FFFFFF;">专业：因为专注，所以专业。十年磨砺，成就一剑！</span><br />\r\n<span style="color:#333333;font-family:tahoma, arial, sans-serif;background-color:#FFFFFF;">透明：坚信阳光是最好的防腐剂。</span></span>\r\n</p>\r\n<p>\r\n	<span style="color:#333333;font-family:tahoma, arial, sans-serif;background-color:#FFFFFF;"><span style="color:#333333;font-family:tahoma, arial, sans-serif;background-color:#FFFFFF;">—打造自由轻松诚信的投融资平台</span><br />\r\n</span>\r\n</p>', '', '<p>\r\n	<table width="950" border="0" cellspacing="0" cellpadding="0" style="margin:0px;color:#333333;font-family:tahoma, arial, sans-serif;font-size:12px;background-color:#FFFFFF;" class="ke-zeroborder">\r\n		<tbody>\r\n			<tr>\r\n				<td width="450" style="font-family:tahoma, arial, sans-serif;">\r\n					<img src="http://www.dingxindai.com/data/upfiles/images/2014-06/04/775_articles_article_1401868046570.jpg" alt="" /> \r\n				</td>\r\n				<td valign="bottom" style="font-family:tahoma, arial, sans-serif;">\r\n					<strong>董事长:吴忠毅</strong><br />\r\n<br />\r\n1977出生，上海交通大学EMBA硕士研究生，中国CEO俱乐部会员，中国民主建国会温州市委员会企业家协会理事，温州市市委纪委五型机关督查员，鹿城区检察院党风廉政监督员，鹿城区环保局党风廉政督查员，鹿城区第八届人大代表，从事多年商业地产投资，名贵物品及奢侈品投资。\r\n				</td>\r\n			</tr>\r\n		</tbody>\r\n	</table>\r\n<span><span style="line-height:normal;background-color:#FFFFFF;"><br />\r\n</span></span>\r\n</p>\r\n<p>\r\n	<span><span style="line-height:normal;background-color:#FFFFFF;">\r\n	<table width="950" border="0" cellspacing="0" cellpadding="0" style="margin:0px;color:#333333;font-family:tahoma, arial, sans-serif;font-size:12px;background-color:#FFFFFF;" class="ke-zeroborder">\r\n		<tbody>\r\n			<tr>\r\n				<td style="font-family:tahoma, arial, sans-serif;">\r\n					<p>\r\n						<img src="http://www.dingxindai.com/data/upfiles/images/2014-06/04/775_articles_article_1401868270938.jpg" alt="" />\r\n					</p>\r\n				</td>\r\n				<td valign="bottom" style="font-family:tahoma, arial, sans-serif;">\r\n					<p>\r\n						<strong>董事总经理：李景文<br />\r\n<br />\r\n</strong>\r\n					</p>\r\n					<p>\r\n						1975年出生，毕业于江西财经大学现代经济管理学院信息管理专业，曾涉及餐饮行业、文化产业、汽车贸易等行业，2005年开始从事宝石玉石类投资。具有专业的投资理念和前瞻的投资眼光。基于互联网不断的发展和对金融行业不断的冲击，2012年李总投资创办鼎信贷平台，并亲自负责公司运作和管理，用专业的知识和能力，推动地区互联网金融行业的发展。\r\n					</p>\r\n				</td>\r\n			</tr>\r\n			<tr>\r\n				<td style="font-family:tahoma, arial, sans-serif;">\r\n					<p>\r\n						<br />\r\n					</p>\r\n					<p>\r\n						<br />\r\n					</p>\r\n					<p>\r\n						<img src="http://www.dingxindai.com/data/upfiles/images/2014-06/04/775_articles_article_1401868315252.jpg" alt="" />\r\n					</p>\r\n				</td>\r\n				<td valign="bottom" style="font-family:tahoma, arial, sans-serif;">\r\n					<p>\r\n						<strong>营运总监：周克宇</strong>\r\n					</p>\r\n					<p>\r\n						1977年出生，毕业于天津师范大学新闻传播学院广告与新闻传播专业，曾多年从事新闻采编、撰稿等工作，至今仍是一名自由撰稿人。随着温州民间借贷的高速发展，2005年开始便涉足融资担保及租赁行业，并多次参与温州本地大型政府项目的融资策划，2012年，当民间借贷遇到互联网的这个契机，周总毅然投资创办鼎信贷平台，并将自己的多年的民间借贷经验和新闻传播相互融合，为鼎信贷平台在宣传和运作上推波助澜。\r\n					</p>\r\n				</td>\r\n			</tr>\r\n		</tbody>\r\n	</table>\r\n<br />\r\n</span></span>\r\n</p>\r\n<p>\r\n	<span><span style="line-height:normal;background-color:#FFFFFF;"><br />\r\n</span></span>\r\n</p>', '<img src="/data/attached/20141211/20141211142017_54575.png" alt="" />', '<p style="color:#333333;font-family:tahoma, arial, sans-serif;background-color:#FFFFFF;">\r\n	<span style="color:#FF6600;">地址：浙江温州市鹿城区新城大道300号新城大厦22楼西首</span>\r\n</p>\r\n<p style="color:#333333;font-family:tahoma, arial, sans-serif;background-color:#FFFFFF;">\r\n	<span style="color:#FF6600;">电话：400-050-8808</span>\r\n</p>\r\n<p style="color:#333333;font-family:tahoma, arial, sans-serif;background-color:#FFFFFF;">\r\n	<span style="color:#FF6600;">网址：<a href="http://www.dingxindai.com/"><span style="color:#FF6600;">www.dingxindai.com</span></a></span>\r\n</p>\r\n<p style="color:#333333;font-family:tahoma, arial, sans-serif;background-color:#FFFFFF;">\r\n	<span style="color:#FF6600;">邮箱：</span><a href="mailto:web@dingxindai.com"><span style="color:#FF6600;">web@dingxindai.com</span></a>\r\n</p>', NULL, 123, '', 0, '刘伟雄', 15767915200, 1418270508),
(51, '<div class="text-content retract" style="margin:0px 0px 20px;padding:0px;border:0px;color:#333333;font-family:''Microsoft Yahei'', 微软雅黑, Tahoma, Arial, Helvetica, STHeiti;background-color:#FFFFFF;">\r\n	<p class="retract" style="text-indent:2em;">\r\n		<span style="line-height:1.5;">上海陆家嘴国际金融资产交易市场股份有限公司（陆金所），在上海市政府的支持下，于2011年9月在上海注册成立，注册资金8.37亿元人民币，是中国平安保险（集团）股份有限公司旗下成员之一，总部位于国际金融中心上海陆家嘴。</span> \r\n	</p>\r\n	<p>\r\n		<br />\r\n	</p>\r\n	<div class="text-content retract" style="margin:0px 0px 20px;padding:0px;border:0px;color:#333333;font-family:''Microsoft Yahei'', 微软雅黑, Tahoma, Arial, Helvetica, STHeiti;background-color:#FFFFFF;">\r\n		<p class="retract" style="text-indent:2em;">\r\n			陆金所将结合金融全球化发展与信息技术创新手段，以健全的风险管控体系为基础，为广大机构、企业与合格投资者等提供专业、高效、安全的综合性金融资产交易相关服务及投融资顾问服务，致力于通过优质服务及不断的交易品种与交易组织模式创新，提升交易效率，优化金融资产配置，成为中国领先并具有重要国际影响力的金融资产交易服务平台。\r\n		</p>\r\n	</div>\r\n	<div class="pageTitle" style="margin:0px 0px 10px;padding:0px;border:0px;font-weight:bold;color:#333333;font-family:''Microsoft Yahei'', 微软雅黑, Tahoma, Arial, Helvetica, STHeiti;background-color:#FFFFFF;">\r\n		业务范围涵盖：\r\n	</div>\r\n	<div class="text-content" style="margin:0px 0px 20px;padding:0px;border:0px;color:#333333;font-family:''Microsoft Yahei'', 微软雅黑, Tahoma, Arial, Helvetica, STHeiti;background-color:#FFFFFF;">\r\n		<p>\r\n			● 金融创新产品的咨询、开发、设计、交易等服务；\r\n		</p>\r\n		<p>\r\n			● 金融类应用软件开发、电子商务；\r\n		</p>\r\n		<p>\r\n			● 金融市场调研及数据分析；\r\n		</p>\r\n		<p>\r\n			● 两大平台：\r\n		</p>\r\n		<p>\r\n			<br />\r\n		</p>\r\n		<p class="retract" style="text-indent:2em;">\r\n			<span class="retractTitle" style="font-weight:bold;">网络投融资平台</span>（<a href="http://www.lufax.com/" target="_blank">www.lufax.com</a>）: 2012年3月正式上线运营，作为中国平安集团倾力打造的网络投融资平台，结合全球金融发展与互联网技术创新，在健全的风险管控体系基础上，为中小企业及个人客户提供专业、可信赖的投融资服务，实现财富增值；\r\n		</p>\r\n		<p class="retract" style="text-indent:2em;">\r\n			<span class="retractTitle" style="font-weight:bold;">金融资产交易服务平台</span>（<a href="http://www.lfex.com/" target="_blank">www.lfex.com</a>）: 致力于通过优质服务及不断的交易品种与交易组织模式创新，提升交易效率，优化金融资产配置，为广大机构、企业和合格投资者等提供专业、高效、安全的综合性金融资产交易相关服务及投融资顾问服务。\r\n		</p>\r\n		<p class="retract" style="text-indent:2em;">\r\n			<img src="/data/attached/20141211/20141211151338_98741.jpg" alt="" /> \r\n		</p>\r\n		<p>\r\n			<br />\r\n		</p>\r\n	</div>\r\n	<h3 style="color:#fff;background-color:#FFFFFF;font-size:16px;font-family:''Microsoft Yahei'', 微软雅黑, Tahoma, Arial, Helvetica, STHeiti;">\r\n		<span style="background-color:#E53333;">公司愿景</span> \r\n	</h3>\r\n	<div class="text-content " style="margin:0px 0px 20px;padding:0px;border:0px;color:#333333;font-family:''Microsoft Yahei'', 微软雅黑, Tahoma, Arial, Helvetica, STHeiti;background-color:#FFFFFF;">\r\n		<p class="retract" style="text-indent:2em;">\r\n			结合全球金融发展与互联网技术，通过风险管理领域的多年实践经验和互联网领域的高端人才积累，为资产提供者和投资者创建一个中国领先的开放，便捷，安全的投融资平台，为个人财富增值、为中小企业提供融资服务、为机构金融资产创造流动性，增加资金效益。\r\n		</p>\r\n	</div>\r\n	<div class="line" style="margin:50px 0px 20px;padding:0px;color:#333333;font-family:''Microsoft Yahei'', 微软雅黑, Tahoma, Arial, Helvetica, STHeiti;background-color:#FFFFFF;">\r\n	</div>\r\n	<h3 style="color:#FFFFFF;background-color:#FFFFFF;font-size:16px;font-family:''Microsoft Yahei'', 微软雅黑, Tahoma, Arial, Helvetica, STHeiti;">\r\n		<span style="background-color:#E53333;">公司荣誉</span> \r\n	</h3>\r\n	<div class="honor-warp" style="margin:30px 0px 20px 20px;padding:0px;color:#333333;font-family:''Microsoft Yahei'', 微软雅黑, Tahoma, Arial, Helvetica, STHeiti;background-color:#FFFFFF;">\r\n		<div class="honor-content" style="margin:0px;padding:0px 0px 0px 30px;border:0px;">\r\n			<span class="date" style="background-color:#9E9E9E;line-height:16px;color:#FFFFFF;">2013.12</span>&nbsp;<span>稳盈-安e贷荣获2013腾讯互联网金融评选“2013互联网金融年度产品大奖”；</span> \r\n		</div>\r\n		<div class="honor-content" style="margin:0px;padding:0px 0px 0px 30px;border:0px;">\r\n			<span class="date" style="background-color:#9E9E9E;line-height:16px;color:#FFFFFF;">2013.12</span>&nbsp;<span>荣获《华夏时报》金蝉奖评选“2013最佳互联网金融平台奖”单项奖；</span> \r\n		</div>\r\n		<div class="honor-content" style="margin:0px;padding:0px 0px 0px 30px;border:0px;">\r\n			<span class="date" style="background-color:#9E9E9E;line-height:16px;color:#FFFFFF;">2013.12</span>&nbsp;<span>被授予中国互联网新闻中心主办的“大国远见”财经峰会“大国远见.新兴力量”奖；</span> \r\n		</div>\r\n		<div class="honor-content" style="margin:0px;padding:0px 0px 0px 30px;border:0px;">\r\n			<span class="date" style="background-color:#9E9E9E;line-height:16px;color:#FFFFFF;">2013.12</span>&nbsp;<span>荣获《第一财经日报》发起和主办的第一财经金融价值榜评选“最佳金融创新企业”奖项；</span> \r\n		</div>\r\n		<div class="honor-content" style="margin:0px;padding:0px 0px 0px 30px;border:0px;">\r\n			<span class="date" style="background-color:#9E9E9E;line-height:16px;color:#FFFFFF;">2013.12</span>&nbsp;<span>中国支付清算协会互联网金融专业委员会在北京成立，董事长计葵生作为行业唯一代表，入选互联网金融专业委员会专家；</span> \r\n		</div>\r\n		<div class="honor-content" style="margin:0px;padding:0px 0px 0px 30px;border:0px;">\r\n			<span class="date" style="background-color:#9E9E9E;line-height:16px;color:#FFFFFF;">2013.11</span>&nbsp;<span>董事长计葵生荣获“2013年中国互联网金融十大领军人物”称号；</span> \r\n		</div>\r\n		<div class="honor-content" style="margin:0px;padding:0px 0px 0px 30px;border:0px;">\r\n			<span class="date" style="background-color:#9E9E9E;line-height:16px;color:#FFFFFF;">2013.11</span>&nbsp;<span>作为P2P行业的代表企业荣膺《经济观察报》主办的2012-2013年度中国卓越金融奖评选“年度卓越新锐金融机构奖”；</span> \r\n		</div>\r\n		<div class="honor-content" style="margin:0px;padding:0px 0px 0px 30px;border:0px;">\r\n			<span class="date" style="background-color:#9E9E9E;line-height:16px;color:#FFFFFF;">2013.11</span>&nbsp;<span>荣获浙江金融办、《都市快报》等发起的金融企业评选，荣获“最佳网络投资平台”奖；</span> \r\n		</div>\r\n		<div class="honor-content" style="margin:0px;padding:0px 0px 0px 30px;border:0px;">\r\n			<span class="date" style="background-color:#9E9E9E;line-height:16px;color:#FFFFFF;">2013.08</span>&nbsp;<span>当选中国互联网金融工作委员会副主任委员单位；</span> \r\n		</div>\r\n		<div class="honor-content" style="margin:0px;padding:0px 0px 0px 30px;border:0px;">\r\n			<span class="date" style="background-color:#9E9E9E;line-height:16px;color:#FFFFFF;">2013.08</span>&nbsp;<span>获得中国互联网协会企业信用评价中心进行统一评价的AAA级认证，AAA级为企业信用评级的最高级，平安陆金所是获得此最高评级的首家互联网投融资平台；</span> \r\n		</div>\r\n		<div class="honor-content" style="margin:0px;padding:0px 0px 0px 30px;border:0px;">\r\n			<span class="date" style="background-color:#9E9E9E;line-height:16px;color:#FFFFFF;">2013.05</span>&nbsp;<span>荣获“中国投资理财行业十大影响力品牌”、“中国网络借贷服务公众满意典范机构”两大奖项；</span> \r\n		</div>\r\n		<div class="honor-content" style="margin:0px;padding:0px 0px 0px 30px;border:0px;">\r\n			<span class="date" style="background-color:#9E9E9E;line-height:16px;color:#FFFFFF;">2012.12</span>&nbsp;<span>董事长计葵生先生荣获“2012沪上十大金融创新人物”称号；</span> \r\n		</div>\r\n		<div class="honor-content" style="margin:0px;padding:0px 0px 0px 30px;border:0px;">\r\n			<span class="date" style="background-color:#9E9E9E;line-height:16px;color:#FFFFFF;">2012.12</span>&nbsp;<span>当选由上海市经信委牵头成立的上海网络信贷服务业企业联盟秘书长单位；</span> \r\n		</div>\r\n	</div>\r\n	<p>\r\n		<br />\r\n	</p>\r\n	<p>\r\n		<br />\r\n	</p>\r\n	<p>\r\n		<br />\r\n	</p>\r\n</div>', '', '<div class="nor-content" style="margin:10px 0px 20px;padding:0px;border:0px;color:#333333;font-family:''Microsoft Yahei'', 微软雅黑, Tahoma, Arial, Helvetica, STHeiti;background-color:#FFFFFF;">\r\n	<span class="name" style="font-size:16px;font-weight:700;">计葵生先生</span>&nbsp;<span class="jobTitle" style="font-size:16px;">陆金所董事长</span>\r\n	<div class="text-content" style="margin:0px 0px 20px;padding:0px;border:0px;">\r\n		计葵生先生现任陆金所董事长。历任麦肯锡大中华地区董事，台湾台新金融控股公司首席运营执行官，在国际金融投资领域拥有超过20年的跨国工作经验，对银行消费信用业务的建立及亚洲银行业的发展有独到见解，曾荣获“2012沪上十大金融创新人物”、“2013年中国互联网金融十大领军人物”称号。\r\n	</div>\r\n</div>\r\n<div class="nor-content" style="margin:10px 0px 20px;padding:0px;border:0px;color:#333333;font-family:''Microsoft Yahei'', 微软雅黑, Tahoma, Arial, Helvetica, STHeiti;background-color:#FFFFFF;">\r\n	<span class="name" style="font-size:16px;font-weight:700;">戴修宪先生</span>&nbsp;<span class="jobTitle" style="font-size:16px;">陆金所副总经理</span>\r\n	<div class="text-content" style="margin:0px 0px 20px;padding:0px;border:0px;">\r\n		戴修宪先生于2011年11月加入陆金所，主要负责金融产品开发、营销及品牌管理。戴修宪先生历任当当网高级副总裁、雅虎电子商务平台负责人、eBay、埃森哲及Oracle等公司项目负责人，是带领当当网成为中国第一家盈利B2C电子商务上市公司的核心高管之一。\r\n	</div>\r\n</div>\r\n<div class="nor-content" style="margin:10px 0px 20px;padding:0px;border:0px;color:#333333;font-family:''Microsoft Yahei'', 微软雅黑, Tahoma, Arial, Helvetica, STHeiti;background-color:#FFFFFF;">\r\n	<span class="name" style="font-size:16px;font-weight:700;">樊方智先生</span>&nbsp;<span class="jobTitle" style="font-size:16px;">陆金所副总经理</span>\r\n	<div class="text-content" style="margin:0px 0px 20px;padding:0px;border:0px;">\r\n		樊方智先生历任东亚银行台湾区（台北及高雄分行）副行长、华一银行总行行助兼苏州分行行长等职，拥有20余年银行管理经验，曾在台资、港资、外资三种银行体系中任职并多次拿下业务冠军。\r\n	</div>\r\n</div>\r\n<div class="nor-content" style="margin:10px 0px 20px;padding:0px;border:0px;color:#333333;font-family:''Microsoft Yahei'', 微软雅黑, Tahoma, Arial, Helvetica, STHeiti;background-color:#FFFFFF;">\r\n	<span class="name" style="font-size:16px;font-weight:700;">石京魁先生</span>&nbsp;<span class="jobTitle" style="font-size:16px;">陆金所首席人力资源执行官</span>\r\n	<div class="text-content" style="margin:0px 0px 20px;padding:0px;border:0px;">\r\n		石京魁先生历任平安国际融资租赁有限公司总经理助理、平安人寿上海分公司总经理、平安人寿深圳分公司总经理、平安集团人力资源部副总经理兼上海分部总经理等职，在人力资源管理方面有着丰富经验，曾荣获“中国十大保险经理人”、“平安集团十佳杰出经理人”、“平安二级勋章”等荣誉。\r\n	</div>\r\n</div>\r\n<div class="nor-content" style="margin:10px 0px 20px;padding:0px;border:0px;color:#333333;font-family:''Microsoft Yahei'', 微软雅黑, Tahoma, Arial, Helvetica, STHeiti;background-color:#FFFFFF;">\r\n	<span class="name" style="font-size:16px;font-weight:700;">杨晓冬先生</span>&nbsp;<span class="jobTitle" style="font-size:16px;">陆金所副总经理</span>\r\n	<div class="text-content" style="margin:0px 0px 20px;padding:0px;border:0px;">\r\n		杨晓冬先生历任美国富达投资中国总裁、富达国际亚太常务理事及首席信息官，美国富达投资集团副总裁及多个高管职务。杨晓冬先生在国际金融投资领域有超过20年以上的从业资历，并在基金及投资行业业务管理、新业务拓展、系统开发及管理、中后台运营及信息安全管理等方面有着丰富的管理经验。\r\n	</div>\r\n</div>\r\n<div class="nor-content" style="margin:10px 0px 20px;padding:0px;border:0px;color:#333333;font-family:''Microsoft Yahei'', 微软雅黑, Tahoma, Arial, Helvetica, STHeiti;background-color:#FFFFFF;">\r\n	<span class="name" style="font-size:16px;font-weight:700;">姚志平先生</span>&nbsp;<span class="jobTitle" style="font-size:16px;">陆金所副总经理</span>\r\n	<div class="text-content" style="margin:0px 0px 20px;padding:0px;border:0px;">\r\n		姚志平先生分管陆金所公司金融产品风险管理中心，历任台新国际商业银行资深副总经理、美国HSBC资深授信/风险管理经理等职，在风控领域有20多年的专业经验。\r\n	</div>\r\n</div>\r\n<div class="nor-content" style="margin:10px 0px 20px;padding:0px;border:0px;color:#333333;font-family:''Microsoft Yahei'', 微软雅黑, Tahoma, Arial, Helvetica, STHeiti;background-color:#FFFFFF;">\r\n	<span class="name" style="font-size:16px;font-weight:700;">杨冀川先生</span>&nbsp;<span class="jobTitle" style="font-size:16px;">陆金所副总经理</span>\r\n	<div class="text-content" style="margin:0px 0px 20px;padding:0px;border:0px;">\r\n		杨冀川先生历任中信证券股份有限公司战略规划部负责人、巴克莱资本新兴市场固定收益/外汇及商品部门结构化组的北亚区负责人等职，此前曾供职于嘉实国际、花旗银行与美联证券。在风险量化分析、信用衍生产品、结构化与证券化产品等领域拥有丰富经验。\r\n	</div>\r\n</div>\r\n<div class="nor-content" style="margin:10px 0px 20px;padding:0px;border:0px;color:#333333;font-family:''Microsoft Yahei'', 微软雅黑, Tahoma, Arial, Helvetica, STHeiti;background-color:#FFFFFF;">\r\n	<span class="name" style="font-size:16px;font-weight:700;">楼晓岸女士</span>&nbsp;<span class="jobTitle" style="font-size:16px;">陆金所副总经理</span>\r\n	<div class="text-content" style="margin:0px 0px 20px;padding:0px;border:0px;">\r\n		楼晓岸女士历任北京金融资产交易所副总裁、中国昆仑银行总行发展规划部副总经理、中国进出口银行总行公司业务处副处长、中国华融资产管理公司总部经营管理部项目负责人等职，具有丰富的机构间金融资产交易业务运作经验，是高级经济师、国家首批企业法律顾问。\r\n	</div>\r\n</div>\r\n<div class="nor-content" style="margin:10px 0px 20px;padding:0px;border:0px;color:#333333;font-family:''Microsoft Yahei'', 微软雅黑, Tahoma, Arial, Helvetica, STHeiti;background-color:#FFFFFF;">\r\n	<span class="name" style="font-size:16px;font-weight:700;">倪荣庆先生</span>&nbsp;<span class="jobTitle" style="font-size:16px;">陆金所副总经理</span>\r\n	<div class="text-content" style="margin:0px 0px 20px;padding:0px;border:0px;">\r\n		倪荣庆先生1995年加入中国平安至今，历任平安养老险总经理助理、平安寿险上海分公司副总经理等职务。逾20年金融服务业执行及管理经验，具有丰富的市场营销及项目推广经验，善于精细经营及开拓创新。\r\n	</div>\r\n</div>\r\n<div class="nor-content" style="margin:10px 0px 20px;padding:0px;border:0px;color:#333333;font-family:''Microsoft Yahei'', 微软雅黑, Tahoma, Arial, Helvetica, STHeiti;background-color:#FFFFFF;">\r\n	<span class="name" style="font-size:16px;font-weight:700;">杨学连先生</span>&nbsp;<span class="jobTitle" style="font-size:16px;">陆金所副总经理</span>&nbsp;<span class="jobTitle" style="font-size:16px;">董事会秘书长</span>\r\n	<div class="text-content" style="margin:0px 0px 20px;padding:0px;border:0px;">\r\n		杨学连先生历任平安养老险股份有限公司副总经理，平安保险重庆分公司副总经理（主持工作）、战略发展中心主任助理、团险副总监等职务，1997年获得中国平安保险总公司评聘的高级经济师职称。\r\n	</div>\r\n</div>\r\n<div class="nor-content" style="margin:10px 0px 20px;padding:0px;border:0px;color:#333333;font-family:''Microsoft Yahei'', 微软雅黑, Tahoma, Arial, Helvetica, STHeiti;background-color:#FFFFFF;">\r\n	<span class="name" style="font-size:16px;font-weight:700;">张涛先生</span>&nbsp;<span class="jobTitle" style="font-size:16px;">陆金所副总经理</span>\r\n	<div class="text-content" style="margin:0px 0px 20px;padding:0px;border:0px;">\r\n		张涛先生历任渤海国际保理有限公司董事长兼总经理、IBM保理公司运营经理、美国太阳计算机系统（中国）有限公司全球金融服务部中国区经理、远东国际租赁有限公司华北区租赁业务经理等职，在商业保理及融资租赁、国内外贸易融资领域拥有超过15年的运营及管理经验。\r\n	</div>\r\n</div>', NULL, '<div class="pageTitle" style="margin:0px 0px 10px;padding:0px;border:0px;font-weight:bold;color:#333333;font-family:''Microsoft Yahei'', 微软雅黑, Tahoma, Arial, Helvetica, STHeiti;background-color:#FFFFFF;">\r\n	<i class="icon-about tel">&nbsp;</i>电话联系\r\n</div>\r\n<div class="text-content" style="margin:0px 0px 20px;padding:0px 0px 20px;border:0px;color:#333333;font-family:''Microsoft Yahei'', 微软雅黑, Tahoma, Arial, Helvetica, STHeiti;background-color:#FFFFFF;">\r\n	<p>\r\n		全国服务热线：4001 6666 18，0755-61864618\r\n	</p>\r\n	<p>\r\n		人工服务时间：每日8：00-22：00\r\n	</p>\r\n</div>\r\n<div class="pageTitle" style="margin:0px 0px 10px;padding:0px;border:0px;font-weight:bold;color:#333333;font-family:''Microsoft Yahei'', 微软雅黑, Tahoma, Arial, Helvetica, STHeiti;background-color:#FFFFFF;">\r\n	<i class="icon-about msg">&nbsp;</i>客户留言\r\n</div>\r\n<div class="text-content" style="margin:0px 0px 20px;padding:0px 0px 20px;border:0px;color:#333333;font-family:''Microsoft Yahei'', 微软雅黑, Tahoma, Arial, Helvetica, STHeiti;background-color:#FFFFFF;">\r\n	<p>\r\n		如有任何建议和意见，请<a href="http://www.lufax.com/help/help_service_feedback.html" target="_blank">在此留言</a>，我们会在2个工作日内联系您！\r\n	</p>\r\n</div>', NULL, 121, '', 0, '刘伟雄', 15767915200, 1418270621),
(52, '<div style="margin:0px auto;color:#000000;font-family:微软雅黑, YaHei, tahoma, arial, ''Hiragino Sans GB'', 宋体;font-size:medium;font-style:normal;font-weight:normal;text-align:start;">\r\n	<p class="fontW" style="font-weight:bold;color:#333333;font-size:16px;text-indent:2em;">\r\n		北京同城翼龙网络科技有限公司\r\n	</p>\r\n	<p style="color:#333333;font-size:16px;text-indent:2em;">\r\n		北京同城翼龙网络科技有限公司（以下简称翼龙贷）作为联想控股战略投资企业，是国内首倡“同城O2O”模式的网络借贷平台。翼龙贷致力于成为一家卓越的互联网金融企业，为三农金融、普惠金融添加助力。\r\n	</p>\r\n	<p style="color:#333333;font-size:16px;text-indent:2em;">\r\n		翼龙贷成立于2007年，是国内P2P行业中第一批探索者，总部位于北京。目前已在全国数百个城市设立运营中心，覆盖超过1000个区县，并计划在未来快速拓展至3000个区县。翼龙贷旨在为广大三农、小微企业主提供P2P借贷服务，平台本身不是借贷主体，而是信息服务者、撮合者以及风险控制者，为大众提供低门槛、能触及、低成本、高效率、安全可靠的融投资新渠道，满足借贷用户的资金需求。\r\n	</p>\r\n	<p style="color:#333333;font-size:16px;text-indent:2em;">\r\n		翼龙贷在2012年成为国家首个金融改革试点的正规金融服务企业，并且也是首个在营业执照经营范围里面加入“民间借贷撮合业务服务”的网络信贷企业。翼龙贷首创“同城借贷O2O”模式，建立了全方位、多层次的风控管理体系。2013年5月，翼龙贷首次被《新闻联播》深度报道，掀起互联网金融热潮，随后又陆续九次被央视报道，成为行业发展的标杆企业。2014年11月获得联想控股战略投资，并获得资本、品牌、管理和关键人才等诸多助力，驶入发展快车道。\r\n	</p>\r\n	<p style="color:#333333;font-size:16px;text-indent:2em;">\r\n		翼龙贷是中国互联网金融协会成员单位，翼龙贷是中国支付清算协会互联网金融专业委员会发起单位，以及互联网金融千人会发起单位。翼龙贷成立七年以来，通过“同城O2O”模式成功帮助几十万个三农家庭、个体工商户、小微企业主等得到资金支持，同时也帮助理财用户实现了财富增值、财务自由的梦想。\r\n	</p>\r\n</div>\r\n<div style="margin:0px auto;color:#000000;font-family:微软雅黑, YaHei, tahoma, arial, ''Hiragino Sans GB'', 宋体;font-size:medium;font-style:normal;font-weight:normal;text-align:start;">\r\n	<h3 class="mt15" style="font-size:16px;font-weight:normal;color:#333333;background:url(http://www.eloancn.com/page/images/aboutUs_bg.png) 0px -50px repeat-x;">\r\n		<span class="wd181" style="font-size:20px;background:url(http://www.eloancn.com/page/images/aboutUs_bg.png) no-repeat;">工作原理及流程</span>\r\n	</h3>\r\n	<p style="color:#333333;font-size:16px;text-indent:2em;">\r\n		翼龙贷网P2P网络借贷平台，提供互联网环境下点对点、个人对个人的借贷服务，平台本身不是借贷主体，而是信息服务者、撮合者以及风险控制者，为大众提供低门槛、能触及、低成本、高效率、安全可靠的融投资新渠道，满足借贷用户的资金需求，同时帮助投资用户实现财富增值，实现双向普惠。\r\n	</p>\r\n	<p style="color:#333333;font-size:16px;text-indent:2em;">\r\n		翼龙贷网工作原理及流程：\r\n	</p>\r\n	<p class="textC" style="text-align:center;color:#333333;font-size:16px;text-indent:2em;">\r\n		<img src="http://www.eloancn.com/page/images/aboutUs01.png?v=1" alt="" style="border:0px none;" />\r\n	</p>\r\n</div>', '', '<div class="intro" style="margin:0px auto;color:#000000;font-family:微软雅黑, YaHei, tahoma, arial, ''Hiragino Sans GB'', 宋体;font-size:medium;font-style:normal;font-weight:normal;text-align:start;background-color:#FFFFFF;">\r\n	<img src="http://www.eloancn.com/page/images/ceo_wang.jpg" alt="翼龙贷董事长王思聪" class="img-bd fl ml15 mt25" style="border:1px solid #C7C7C7;" /> \r\n	<div class="fl mt20 ml10 posiR" style="margin:20px auto 0px 10px;">\r\n		<h4 class="s-ttl" style="font-size:16px;font-weight:bold;">\r\n			<span class="fl">王思聪</span><span class="fr">翼龙贷董事长</span> \r\n		</h4>\r\n		<p style="color:#333333;font-size:14px;text-indent:0px;">\r\n			王思聪，温州金改标杆企业翼龙贷创始人、董事长。\r\n		</p>\r\n		<p class="color7" style="color:#777777;font-size:14px;text-indent:0px;">\r\n			王思聪1997年试水互联网创业，2002年从事民间借贷撮合服务，2007年创立“中国在线贷款网”，2009年网站模式调整，转变为完全在线上撮合借贷交易，即P2P网络借贷模式，并正式更名为“翼龙贷”。2013年，王思聪与互联网金融业内专家、创业者联合创立了互联网金融千人会。2014年，王思聪当选互联网金融新锐人物，翼龙贷获评互联网金融新锐企业。\r\n		</p>\r\n<br />\r\n	</div>\r\n</div>\r\n<div class="intro-more hide" style="margin:0px auto;color:#000000;font-family:微软雅黑, YaHei, tahoma, arial, ''Hiragino Sans GB'', 宋体;font-size:medium;font-style:normal;font-weight:normal;text-align:start;background-color:#FFFFFF;">\r\n	<p style="color:#777777;font-size:14px;text-indent:2em;">\r\n		王思聪带领翼龙贷成功地对国外P2P网贷模式进行了本土化创新，在全国首创“同城O2O”借贷模式，将“线上信息撮合”与“线下风险防控”完美结合，有效提升了风控质量，不仅为互联网网民提供高效、透明、安全的借贷和理财平台，也让全国各地广大离网人群、尤其是“三农”家庭能够享受互联网带来的普惠金融，将互联网金融服务带到了全国各地县乡镇村，覆盖到了传统金融服务体系的薄弱环节。\r\n	</p>\r\n	<p style="color:#777777;font-size:14px;text-indent:2em;">\r\n		王思聪对互联网、民间借贷和电子商务领域有着长达十余年的探索经验，在国内主流P2P管理团队中对县域经济理解最为深刻。王思聪经过7年的努力，成功地把翼龙贷打造成了一支成熟、团结、有梦想的学习型团队。翼龙贷目前已在全国近200个城市设立运营中心，覆盖超过1000个区县，已经为全国几十万三农家庭提供了规范、及时的借贷服务。\r\n	</p>\r\n	<p style="color:#777777;font-size:14px;text-indent:2em;">\r\n		<b>个人简历</b> \r\n	</p>\r\n	<p style="color:#777777;font-size:14px;text-indent:2em;">\r\n		1965年，出生于河北。\r\n	</p>\r\n	<p style="color:#777777;font-size:14px;text-indent:2em;">\r\n		1997年，在与外国人洽谈生意中首次接触互联网，创立CE88电子贸易网站，成为中国互联网浪潮的第一波试水者，此时马云的阿里巴巴还没有成立。\r\n	</p>\r\n	<p style="color:#777777;font-size:14px;text-indent:2em;">\r\n		2002年，进入北京大学学习金融，之后便开始从事民间借贷撮合业务，先后成立了投资咨询公司：一是做汽车金融按揭，二是房屋抵押贷款中介，三是投资咨询，四是金融信息产品。\r\n	</p>\r\n	<p style="color:#777777;font-size:14px;text-indent:2em;">\r\n		2007年，彻底向互联网金融转型，创立了一个金融信息网站，线上发布贷款信息，线下撮合民间借贷，名字叫“中国在线贷款网”，也就是翼龙贷网的前身。\r\n	</p>\r\n	<p style="color:#777777;font-size:14px;text-indent:2em;">\r\n		2009年，网站模式调整，转变为完全在线上撮合借贷交易，即P2P网络借贷模式，并正式更名为“翼龙贷网”。\r\n	</p>\r\n	<p style="color:#777777;font-size:14px;text-indent:2em;">\r\n		2011年10月，翼龙贷网P2P平台正式上线，成为温州金改试点的互联网金融企业。\r\n	</p>\r\n	<p style="color:#777777;font-size:14px;text-indent:2em;">\r\n		2012年，翼龙贷在王思聪带领下首批入驻国家金改单位温州民间借贷登记服务中心；央视对翼龙贷网取得了可从事民间借贷撮合业务工商执照进行了报道。\r\n	</p>\r\n	<p style="color:#777777;font-size:14px;text-indent:2em;">\r\n		2013年初，翼龙贷作为行业蓝本，为监管机构献言建策，得到了高层的支持和认可，并予以推广。\r\n	</p>\r\n	<p style="color:#777777;font-size:14px;text-indent:2em;">\r\n		2013年5月11日，央视《新闻联播》等栏目深度报道翼龙贷模式，至此翼龙贷网以及整个互联网金融也得到了主流媒体和高层的关注和认可。\r\n	</p>\r\n	<p style="color:#777777;font-size:14px;text-indent:2em;">\r\n		2013年，王思聪与互联网金融业内专家、创业者联合创立了互联网金融千人会。\r\n	</p>\r\n	<p style="color:#777777;font-size:14px;text-indent:2em;">\r\n		2014年，王思聪当选互联网金融新锐人物，翼龙贷网获评互联网金融新锐企业。\r\n	</p>\r\n	<p style="color:#777777;font-size:14px;text-indent:2em;">\r\n		翼龙贷作为国务院批准的中国互联网金融协会发起单位（筹）。\r\n	</p>\r\n	<p style="color:#777777;font-size:14px;text-indent:2em;">\r\n		在王思聪的带领下，翼龙贷网实现了飞跃式发展，已成为中国互联网金融领军企业、P2P标杆平台，获得“互联网金融十大品牌”、“互联网金融最具投资价值企业”等多项殊荣，并获得央行认可，成为中国支付清算协会互联网金融专业委员会发起单位。\r\n	</p>\r\n</div>\r\n<div class="intro" style="margin:0px auto;color:#000000;font-family:微软雅黑, YaHei, tahoma, arial, ''Hiragino Sans GB'', 宋体;font-size:medium;font-style:normal;font-weight:normal;text-align:start;background-color:#FFFFFF;">\r\n	<img src="http://www.eloancn.com/page/images/ceo_mao.jpg" alt="翼龙贷总裁毛向前" class="img-bd fl ml15 mt25" style="border:1px solid #C7C7C7;" /> \r\n	<div class="fl mt20 ml10 posiR" style="margin:20px auto 0px 10px;">\r\n		<h4 class="s-ttl" style="font-size:16px;font-weight:bold;">\r\n			<span class="fl">毛向前</span><span class="fr">翼龙贷总裁</span> \r\n		</h4>\r\n		<p style="color:#333333;font-size:14px;text-indent:0px;">\r\n			毛向前，毕业于天津大学机械系，硕士学位，中欧国际工商管理学院EMBA，曾任联想控股战略投资部高级总监，现任翼龙贷总裁一职。\r\n		</p>\r\n		<p class="color7" style="color:#777777;font-size:14px;text-indent:0px;">\r\n			毛向前先生曾担任联想武汉分公司总经理、信息产品事业部副总经理，曾任神州数码科技发展公司总裁，曾任佳杰科技（中国）有限公司CEO，曾任联想控股战略投资部高级总监等关键职位，在IT行业拥有多年经营、管理的成功经验，帮助神州数码、佳杰科技度过了艰难的创业期并驶入发展快车道。毛向前先生对翼龙贷模式有着深度研究，并且自身拥有丰富的加盟运作经验。今后将为翼龙贷带来更加完善的管理理念并丰富业务布局。\r\n		</p>\r\n<a>收起&nbsp;↑</a> \r\n	</div>\r\n</div>\r\n<div class="intro-more hide" style="margin:0px auto;color:#000000;font-family:微软雅黑, YaHei, tahoma, arial, ''Hiragino Sans GB'', 宋体;font-size:medium;font-style:normal;font-weight:normal;text-align:start;background-color:#FFFFFF;">\r\n	<p style="color:#777777;font-size:14px;text-indent:2em;">\r\n		<b>个人简历</b> \r\n	</p>\r\n	<p style="color:#777777;font-size:14px;text-indent:2em;">\r\n		毛向前于1989年加盟联想集团，先后任联想武汉分公司总经理、信息产品事业部副总经理等职。\r\n	</p>\r\n	<p style="color:#777777;font-size:14px;text-indent:2em;">\r\n		2001年，毛向前担任神州数码副总裁，主管通用信息产品事业本部。\r\n	</p>\r\n	<p style="color:#777777;font-size:14px;text-indent:2em;">\r\n		2003年，毛向前任神州数码高级副总裁，主管神州数码通用产品分销业务。在神州数码“IT服务”的总方针下，认真研究国内、外分销行业特点，在总结神州数码分销优势的基础上，对供应链进行分析，完善了神州数码的供应链服务理论，强化了通用信息产品事业本部的分销核心竞争力。\r\n	</p>\r\n	<p style="color:#777777;font-size:14px;text-indent:2em;">\r\n		2006年，神码通本组建为“神州数码科技发展公司”，神州数码集团高级副总裁毛向前出任新公司总裁。同时，神州数码不但对通本进行调整，旗下的企业分销、IT服务和银行业务也均将“变身”子公司。\r\n	</p>\r\n	<p style="color:#777777;font-size:14px;text-indent:2em;">\r\n		2009年，毛向前加盟佳杰科技（中国）有限公司，担任CEO一职。佳杰科技2009年的营业额增长率超过了2008年的增长率，尤其是在第三、四季度，在市场回暖复苏的推动下，业绩大幅超出预期。\r\n	</p>\r\n	<p style="color:#777777;font-size:14px;text-indent:2em;">\r\n		2013年，毛向前回到联想控股，出任战略投资部高级总监一职。\r\n	</p>\r\n	<p style="color:#777777;font-size:14px;text-indent:2em;">\r\n		2014年，毛向前加入翼龙贷，出任翼龙贷总裁一职。\r\n	</p>\r\n</div>', NULL, '<h3 style="font-size:16px;font-weight:normal;color:#333333;font-family:微软雅黑, YaHei, tahoma, arial, ''Hiragino Sans GB'', 宋体;font-style:normal;text-align:start;text-indent:0px;background:url(http://www.eloancn.com/page/images/aboutUs_bg.png) 0px -50px repeat-x #FFFFFF;">\r\n	<span class="wd110" style="font-size:20px;background:url(http://www.eloancn.com/page/images/aboutUs_bg.png) no-repeat;">联系我们</span>\r\n</h3>\r\n<p class="service_qq fl" style="color:#333333;font-size:medium;text-indent:2em;font-family:微软雅黑, YaHei, tahoma, arial, ''Hiragino Sans GB'', 宋体;font-style:normal;font-weight:normal;text-align:start;background-color:#FFFFFF;">\r\n	<span style="background:url(http://www.eloancn.com/page/images/aboutUs_bg.png) 0px -127px no-repeat;"></span>\r\n</p>\r\n<p style="color:#333333;font-size:medium;text-indent:2em;font-family:微软雅黑, YaHei, tahoma, arial, ''Hiragino Sans GB'', 宋体;font-style:normal;font-weight:normal;text-align:start;background-color:#FFFFFF;">\r\n	北京总部地址：北京市海淀区信息路甲28号科实大厦A座308（由上地美食城入口进入）&nbsp;&nbsp;&nbsp;&nbsp;100085\r\n</p>\r\n<p style="color:#333333;font-size:medium;text-indent:2em;font-family:微软雅黑, YaHei, tahoma, arial, ''Hiragino Sans GB'', 宋体;font-style:normal;font-weight:normal;text-align:start;background-color:#FFFFFF;">\r\n	媒体合作：15321432735\r\n</p>\r\n<br />\r\n<img class="ml15" src="http://www.eloancn.com/page/images/connect_map.png" style="border:0px none;" /><span style="color:#000000;font-family:微软雅黑, YaHei, tahoma, arial, ''Hiragino Sans GB'', 宋体;font-size:medium;font-style:normal;font-weight:normal;line-height:normal;background-color:#FFFFFF;"></span>\r\n<p style="color:#333333;font-size:medium;text-indent:2em;font-family:微软雅黑, YaHei, tahoma, arial, ''Hiragino Sans GB'', 宋体;font-style:normal;font-weight:normal;text-align:start;background-color:#FFFFFF;">\r\n	温州公司地址：温州市鹿城区东明路东明锦园1-3幢民间借贷服务中心B201&nbsp;B206\r\n</p>\r\n<br />\r\n<img class="ml15" src="http://www.eloancn.com/page/images/map_wenz.png" style="border:0px none;" />', NULL, 124, '', 0, '老刘', 15767915200, 1418270688);
INSERT INTO `k_company_info` (`cpid`, `info`, `mainbissness`, `team`, `voucher`, `contact_us`, `charge`, `uid`, `boss_name`, `boss_mobile`, `operation_name`, `operation_mobile`, `dateline`) VALUES
(53, '<div class="a_rcontc" style="margin:auto;font-size:14px;color:#666666;font-family:微软雅黑, tahoma, arial, 宋体, sans-serif;">\r\n	<p>\r\n		拍拍贷成立于2007年6月，公司全称为“上海拍拍贷金融信息服务有限公司”，总部位于国际金融中心的上海， 是中国第一家P2P（个人对个人）网络信用借贷平台。\r\n	</p>\r\n	<p>\r\n		拍拍贷同时也是第一家由工商部门特批，获得"金融信息服务"资质，从而得到政府认可的互联网金融平台。 拍拍 贷用先进的理念和创新的技术建立了一个安全、高效、诚信、透明的互联网金融平台，规范了个人借贷行为，让 借入者改善生产生活，让借出者增加投资渠道。拍拍贷相信，随着互联网的发展和中国个人信用体系的健全，先进 的理念和创新的技术将给民间借贷带来历史性的变革，拍拍贷将是这场变革的领导者。\r\n	</p>\r\n	<p>\r\n		2012年10月拍拍贷成为首家完成A轮融资网贷平台，获得红杉资本(Sequoia Capital)千万美元级别投资。 2014年4月拍拍贷在北京钓鱼台国宾馆宣布完成B轮融资，国内P2P行业首个完成B轮融资的网贷平台，投资机 构分别为光速安振中国创业投资(Lightspeed China Partners)，红杉资本(Sequoia Capital)及纽交所上市公司诺 亚财富。\r\n	</p>\r\n</div>\r\n<div class="a_concom" style="margin-left:27px;font-family:微软雅黑, tahoma, arial, 宋体, sans-serif;font-size:14px;">\r\n	<img src="http://www.ppdaicdn.com/2014/img/about03.gif" />\r\n</div>\r\n<div class="a_rcontext2 clearfix" style="margin:25px auto auto;font-family:微软雅黑, tahoma, arial, 宋体, sans-serif;font-size:14px;">\r\n	<span class="a_rcont2 " style="font-size:18px;color:#666666;background-color:#FFFFFF;">拍拍贷的使命</span>\r\n</div>\r\n<div class="a_consm" style="margin:30px auto 50px;font-size:14px;color:#666666;font-family:微软雅黑, tahoma, arial, 宋体, sans-serif;">\r\n	<p>\r\n		让天下没有难借的钱。\r\n	</p>\r\n	<p>\r\n		协力中国信用体系的建设，发现中国人的信用价值。\r\n	</p>\r\n</div>\r\n<div class="a_rcontext2 clearfix" style="margin:25px auto auto;font-family:微软雅黑, tahoma, arial, 宋体, sans-serif;font-size:14px;">\r\n	<span class="a_rcont2" style="font-size:18px;color:#666666;background-color:#FFFFFF;">拍拍贷的灵魂</span>\r\n</div>\r\n<div class="a_conlhc" style="margin:30px auto auto;font-size:14px;color:#666666;font-family:微软雅黑, tahoma, arial, 宋体, sans-serif;">\r\n	<p>\r\n		<a>梦想</a>&nbsp;以公益的心态，去实现改变中国，造福人民，创造社会价值的伟大梦想。\r\n	</p>\r\n	<p>\r\n		<a>创新</a>&nbsp;坚持技术创新的方式，以符合自然规律的方式发展，并集中精力把技术创新做到极致。\r\n	</p>\r\n	<p>\r\n		<a>耐心</a>&nbsp;保持耐心，让时间成为我们的武器和朋友，以长远的眼光看待我们的事业。\r\n	</p>\r\n</div>', '', '<p>\r\n	<span class="team_img"><img src="http://www.ppdaicdn.com/2014/img/about/team_img1.jpg" style="border:1px solid #EEEEEE;" /></span><span class="t_Title" style="font-size:22px;line-height:36px;font-family:''Microsoft Yahei'';color:#333333;">张俊<span style="font-size:18px;"> （首席执行官、CEO）</span></span> \r\n</p>\r\n<p style="text-indent:2em;">\r\n	毕业于上海交通大学，通信工程学士，工业工程硕士，通过特许金融分析师认证（CFA Program）二级考试。曾任职于微软全球技术中心和上海微创软件有限公司，担任高级客户经理和高级运营经理。张俊对于网络信贷的业务模式创新、信用评价模型和风险管理体系建设有独到的见解和实践经验，并在企业管理和运营上有着丰富的经验。\r\n</p>\r\n<ul class="clearfix">\r\n	<li style="font-family:''Microsoft Yahei'';">\r\n		中国互联网金融智库首批专家成员\r\n	</li>\r\n	<li style="font-family:''Microsoft Yahei'';">\r\n		2013年度中小微金融创新十大领军人物\r\n	</li>\r\n	<li style="font-family:''Microsoft Yahei'';">\r\n		互联网金融工作委员会专家委员会专家\r\n	</li>\r\n	<li style="font-family:''Microsoft Yahei'';">\r\n		2013年度中国互联网金融年度新锐人物\r\n	</li>\r\n	<li style="font-family:''Microsoft Yahei'';">\r\n		2013年度"上海IT青年十大新锐"\r\n	</li>\r\n	<li style="font-family:''Microsoft Yahei'';">\r\n		上海市青年五四奖章\r\n	</li>\r\n</ul>\r\n<p>\r\n	<br />\r\n</p>\r\n<div class="team_con02 clearfix" style="color:#666666;font-family:微软雅黑, tahoma, arial, 宋体, sans-serif;font-size:14px;">\r\n	<span class="team_img"><img src="http://www.ppdaicdn.com/2014/img/about/team_img2.jpg" style="border:1px solid #EEEEEE;" /></span><span class="t_Title" style="font-size:22px;line-height:36px;font-family:''Microsoft Yahei'';color:#333333;">胡宏辉<span style="font-size:18px;">（执行总裁）</span></span> \r\n	<p style="text-indent:2em;">\r\n		毕业于上海交通大学管理学院和复旦大学经济学院，拥有经济学学士和硕士学位。曾 任职于中国工商银行信贷部门，参与过多个重点融资项目，在银行市场业务方面有丰富经验。胡宏辉拥有律师职业资格，曾在国内著名律师事务所担任律师和高级合伙人，长期从事与金融相关的法律工作。\r\n	</p>\r\n</div>\r\n<div class="team_con03 clearfix" style="color:#666666;font-family:微软雅黑, tahoma, arial, 宋体, sans-serif;font-size:14px;">\r\n	<span class="team_img"><img src="http://www.ppdaicdn.com/2014/img/about/team_img3.jpg" style="border:1px solid #EEEEEE;" /></span><span class="t_Title" style="font-size:22px;line-height:36px;font-family:''Microsoft Yahei'';color:#333333;">李铁铮<span style="font-size:18px;">（首席风险官、CRO）</span></span> \r\n	<p style="text-indent:2em;">\r\n		毕业于上海交通大学，拥有工学学士学位。曾任职于中国民生银行总行中小企业部， 亲历了民生银行小企业信贷业务从无到有、从小到大的整个发展历程，并在此过程中担任了风险管理相关的多个重要岗位。核心关注小额信贷领域的产品设计、风险管理、信用评级、风险定价、信贷工厂、流程和运营管理等。\r\n	</p>\r\n</div>\r\n<div class="team_con04 clearfix" style="color:#666666;font-family:微软雅黑, tahoma, arial, 宋体, sans-serif;font-size:14px;">\r\n	<span class="team_img"><img src="http://www.ppdaicdn.com/2014/img/about/team_img4.jpg" style="border:1px solid #EEEEEE;" /></span><span class="t_Title" style="font-size:22px;line-height:36px;font-family:''Microsoft Yahei'';color:#333333;">周浩<span style="font-size:18px;">（市场副总裁）</span></span> \r\n	<p style="text-indent:2em;">\r\n		毕业于复旦大学文学院/上海大学影视学院，拥有影视策划学士学位。曾任职上海文 广传媒集团，后投身互联网，在快钱支付、安居客房产、淘宝口碑网等互联网领先企业负责市场营销管理，帮助上述企业在奠定了稳定的市场地位和创建了值得尊敬的品牌，对互联网营销拥有丰富的经验。\r\n	</p>\r\n</div>\r\n<div class="team_con05 clearfix" style="color:#666666;font-family:微软雅黑, tahoma, arial, 宋体, sans-serif;font-size:14px;">\r\n	<span class="team_img"><img src="http://www.ppdaicdn.com/2014/img/about/team_img5.jpg" style="border:1px solid #EEEEEE;" /></span><span class="t_Title" style="font-size:22px;line-height:36px;font-family:''Microsoft Yahei'';color:#333333;">王宏<span style="font-size:18px;">（技术副总裁）</span></span> \r\n	<p style="text-indent:2em;">\r\n		毕业于兰州大学数学系计算数学及应用软件专业，理学学士学位；曾任职于国内最知名的网络游戏服务网5173.com，对互联网产品技术研发具有丰富经验。\r\n	</p>\r\n</div>\r\n<div class="team_con06 clearfix" style="color:#666666;font-family:微软雅黑, tahoma, arial, 宋体, sans-serif;font-size:14px;">\r\n	<span class="team_img"><img src="http://www.ppdaicdn.com/2014/img/about/team_img6.jpg" style="border:1px solid #EEEEEE;" /></span><span class="t_Title" style="font-size:22px;line-height:36px;font-family:''Microsoft Yahei'';color:#333333;">Joseph Milana<span style="font-size:18px;">（科学顾问）</span></span> \r\n</div>', '<img src="http://www.ppdaicdn.com/2014/img/about/dashiji.jpg" />', '<span style="font-size:18px;line-height:38px;font-family:''Microsoft Yahei'';color:#003A63;">客户服务</span>\r\n<p style="font-family:''Microsoft Yahei'';font-size:14px;color:#666666;">\r\n	如果您在使用过程中对功能产生疑问或出现错误提示，请与我们的客服人员联系。\r\n</p>\r\n<p style="font-family:''Microsoft Yahei'';font-size:14px;color:#666666;">\r\n	客服电话：<span style="color:#0087E6;">4001-855-955（工作日9:00-18:00）</span>\r\n</p>\r\n<p style="font-family:''Microsoft Yahei'';font-size:14px;color:#666666;">\r\n	在线帮助：<a href="http://help.ppdai.com/" target="_blank">http://help.ppdai.com</a>\r\n</p>\r\n<p style="font-family:''Microsoft Yahei'';font-size:14px;color:#666666;">\r\n	客服中心：<a href="http://www.ppdai.com/consult" target="_blank">http://www.ppdai.com/consult</a>\r\n</p>\r\n<p style="font-family:''Microsoft Yahei'';font-size:14px;color:#666666;">\r\n	客服邮箱：<a href="mailto:kefu@ppdai.com">kefu@ppdai.com</a>（仅无法正常登录网站的情况，其他问题请访问客服中心）\r\n</p>\r\n<p style="font-family:''Microsoft Yahei'';font-size:14px;color:#666666;">\r\n	商务合作，公司战略合作及对外项目合作(平台合作，业务合作，品牌合作等)：<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;021-51870819，<a href="mailto:partner@ppdai.com">partner@ppdai.com</a>\r\n</p>\r\n<p style="font-family:''Microsoft Yahei'';font-size:14px;color:#666666;">\r\n	媒体采访：请提前准备媒体名称、采访提纲、联系方式等信息，发送至&nbsp;<a href="mailto:pr@ppdai.com">pr@ppdai.com</a>\r\n</p>\r\n<span style="font-size:18px;line-height:38px;font-family:''Microsoft Yahei'';color:#003A63;">市场合作 （品牌合作，市场活动合作，广告合作，媒体采访）</span>\r\n<p style="font-family:''Microsoft Yahei'';font-size:14px;color:#666666;">\r\n	联系电话：<span style="color:#0087E6;">021-33191068-8333</span>\r\n</p>\r\n<p style="font-family:''Microsoft Yahei'';font-size:14px;color:#666666;">\r\n	品牌合作，市场活动合作，广告合作-电子邮箱：<a href="mailto:bd@ppdai.com">bd@ppdai.com</a>\r\n</p>\r\n<p style="font-family:''Microsoft Yahei'';font-size:14px;color:#666666;">\r\n	媒体采访-电子邮箱：<a href="mailto:pr@ppdai.com">pr@ppdai.com</a>\r\n</p>\r\n<p style="font-family:''Microsoft Yahei'';font-size:14px;color:#666666;">\r\n	平台合作，业务合作-电子邮箱：<a href="mailto:partner@ppdai.com">partner@ppdai.com</a>\r\n</p>\r\n<span style="font-size:18px;line-height:38px;font-family:''Microsoft Yahei'';color:#003A63;">了解PPDAI.com最新进展？</span>\r\n<p class="clearfix" style="font-family:''Microsoft Yahei'';font-size:14px;color:#666666;">\r\n	<a class="blue" href="http://weibo.com/ppdai07" target="_blank">拍拍贷微博</a><a class="blue" href="http://blog.sina.com.cn/u/1894964897" target="_blank">拍拍贷博客</a><a class="blue" href="http://baike.baidu.com/view/2214866.htm" target="_blank">拍拍贷百度百科</a><a class="blue" href="http://baike.soso.com/v7619913.htm" target="_blank">拍拍贷搜搜百科</a><a class="blue" href="http://site.douban.com/136829/" target="_blank">拍拍贷豆瓣小站</a><a href="http://www.douban.com/group/ppdai/" class="blue" target="_blank">拍拍贷豆瓣小组</a><a href="http://t.qq.com/ppdai_com" class="blue" target="_blank">拍拍贷腾讯微博</a>\r\n</p>\r\n<span style="font-size:18px;line-height:38px;font-family:''Microsoft Yahei'';color:#003A63;">公司地址</span>\r\n<p style="font-family:''Microsoft Yahei'';font-size:14px;color:#666666;">\r\n	公司地址：上海市 浦东新区 郭守敬路498号8号楼1楼\r\n</p>\r\n<p style="font-family:''Microsoft Yahei'';font-size:14px;color:#666666;">\r\n	客服电话：4001-855-955（工作日9:00-18:00）\r\n</p>\r\n<p style="font-family:''Microsoft Yahei'';font-size:14px;color:#666666;">\r\n	业务合作电话：021-51870819（注：以上电话只接收业务合作联系，客户问题请拨打400客服电话或访问客服中心）\r\n</p>', '暂时不收费', 128, '', 0, '刘伟雄', 15767915200, 1418288492),
(54, '<h2 class="h2siz18" style="font-size:18px;color:#6F6F6F;font-family:''Microsoft YaHei'', Arial, Helvetica, sans-serif;">\r\n	公司背景\r\n</h2>\r\n<h3 class="h3sty01" style="font-size:16px;color:#6F6F6F;font-family:''Microsoft YaHei'', Arial, Helvetica, sans-serif;">\r\n	【母公司】\r\n</h3>\r\n<h4 class="logoabout logoabout-ft" style="font-size:0px;font-weight:normal;text-indent:-9999px;color:#6F6F6F;font-family:''Microsoft YaHei'', Arial, Helvetica, sans-serif;">\r\n	富泰金融集团有限公司\r\n</h4>\r\n<p>\r\n	<p>\r\n		浙江信互贷电子商务有限公司的母公司为富泰金融集团。\r\n	</p>\r\n	<p>\r\n		富泰金融集团于1998年在香港创建，以产业投资为主导，在飞机租赁、基础建设项目、结构融资、网络金融等投资项目中屡创先例，创造优厚回报，推动实体经济发展。凭藉专门知识和技术、团队的丰富产业经验及广泛的华资金融网络，集团积极投资于国内及海外国家政府重点支持的项目，在推动当地经济的前提下，以谨慎的投资策略配合创新进取的管理模式，积极开拓产业投资项目。\r\n	</p>\r\n	<p>\r\n		富泰金融除了是项目投资者外，亦亲自参与项目运营及管理，为提供资金和解决方案之馀，更是项目的持份者，务求达成面面皆赢的成果，实现富泰金融推动中国实体金融的目的。\r\n	</p>\r\n	<p>\r\n		富泰金融集团注册资本为一亿美元（相等于约7.8亿港元），截至2013年底，集团及其关联成员公司，包括上市及非上市公司的总资产达25.6亿美元（相等于约200亿港元）。\r\n	</p>\r\n	<p>\r\n		公司网站：<a href="http://www.fpigp.com/" target="_blank" class="Colorblue unl ita">http://www.fpigp.com</a>&nbsp;&nbsp; &nbsp; &nbsp; &nbsp;\r\n	</p>\r\n	<p>\r\n	</p>\r\n</p>\r\n<h4 class="logoabout logoabout-zc" style="font-size:0px;font-weight:normal;text-indent:-9999px;color:#6F6F6F;font-family:''Microsoft YaHei'', Arial, Helvetica, sans-serif;background-color:#FFFFFF;">\r\n	富泰资产管理有限公司\r\n</h4>\r\n<div class="lh26" style="margin:0px;padding:0px;border:0px;color:#6F6F6F;font-family:''Microsoft YaHei'', Arial, Helvetica, sans-serif;font-size:14px;background-color:#FFFFFF;">\r\n	<p>\r\n		富泰资产管理有限公司（“富泰资产”）成立于2000年，是一家国际型资产管理公司和直接投资公司。公司秉承追求卓越的投资业绩和合作伙伴创建共赢的格局。公司主营业务包括自营投资、私募股权投资、资产管理和提供结构性融资解决方案。公司的客户包括机构客户、零售客户和高资产净值人士。富泰资产凭借其对金融市场的深刻了解，良好的投资表现以及广泛且强有力的合作伙伴网路（包括国际银行、金融机构和各级政府等）获得了投资人的肯定。\r\n	</p>\r\n	<p>\r\n		公司网站：<a href="http://fpam.com.hk/" target="_blank" class="Colorblue unl ita">http://fpam.com.hk</a>&nbsp;&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;\r\n	</p>\r\n</div>\r\n<h4 class="logoabout logoabout-zq" style="font-size:0px;font-weight:normal;text-indent:-9999px;color:#6F6F6F;font-family:''Microsoft YaHei'', Arial, Helvetica, sans-serif;background-color:#FFFFFF;">\r\n	中侨融资租赁有限公司\r\n</h4>\r\n<div class="lh26" style="margin:0px;padding:0px;border:0px;color:#6F6F6F;font-family:''Microsoft YaHei'', Arial, Helvetica, sans-serif;font-size:14px;background-color:#FFFFFF;">\r\n	<p>\r\n		中侨融资租赁有限公司（“中侨租赁”）注册于天津，是一间重点服务于国内经济发展产业结构调整的综合性融资租赁公司。公司积极贯彻国家的"请进来"、"走出去"战略，利用其在进出口租赁和跨境融资方面具有的独特优势，为客户提供全方位的融资租赁服务，包括基础设施、进口设备、不动产、船舶交通运输、创新的融资租赁业务等。中侨租赁的股东之一是华侨投资（天津）基金（有限合伙），该基金是在中国侨联和天津侨联的大力支持下，广泛团结全球华侨、侨眷和海外侨胞的力量设立的。\r\n	</p>\r\n	<p>\r\n		公司网站：<a href="http://www.safll.cn/" target="_blank" class="Colorblue unl ita">http://www.safll.cn</a>&nbsp;&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;\r\n	</p>\r\n</div>\r\n	</div>', '', NULL, NULL, '<p>\r\n	<span style="color:#222222;font-family:Consolas, ''Lucida Console'', monospace;line-height:normal;background-color:#FFFFFF;">总 机：0571-87711030 &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;传 真：0571-87711039</span>\r\n</p>\r\n<p>\r\n	<span style="color:#222222;font-family:Consolas, ''Lucida Console'', monospace;line-height:normal;background-color:#FFFFFF;"><span style="color:#222222;font-family:Consolas, ''Lucida Console'', monospace;line-height:normal;background-color:#FFFFFF;">地 址：浙江省 杭州市 滨江区 江晖路1772 苏泊尔大厦9楼 902室（邮编：310051）</span><br />\r\n</span>\r\n</p>', NULL, 130, '', 0, '刘伟雄', 15767915200, 1418351105),
(55, '<p>\r\n	<span style="font-size:16px;">V5金融(</span><a href="http://www.v5jr.com/"><span style="font-size:16px;">www.v5jr.com</span></a><span style="font-size:16px;">)是一家以真实、小额、分散为原则的P2P网络借贷居间平台。由深圳威武金融服务有限公司负责运营，公司位于深圳市福田区彩田南路中深花园大厦A栋A908;公司注册于2014年09月。</span>\r\n</p>\r\n<p>\r\n	<span style="color:#E53333;font-size:16px;">核心：</span><span style="font-size:16px;">V5金融以“风控”为运作核心，缔造最安全的借款业务</span>\r\n</p>\r\n<p>\r\n	<span style="color:#E53333;font-size:16px;">使命：</span><span style="font-size:16px;">创造绿色投资环境，让投资人放心理财</span>\r\n</p>\r\n<p>\r\n	<span style="color:#E53333;font-size:16px;">愿景：</span><span style="font-size:16px;">专注于业务、技术全面创新，做国内领先的互联网金融平台，结合社会不断发展变化而产生的多元化金融需求，助于中、小、微企业及个人发展和实现国民财富增值。</span>\r\n</p>', '', NULL, '<p style="text-indent:2em;color:#434343;font-family:''Microsoft YaHei'', Tahoma, Helvetica, SimSun, sans-serif;font-size:16px;background-color:#FFFFFF;">\r\n	<img src="http://www.v5jr.com/data/upfiles/image/20141124/20141124142138_46353.jpg" alt="" />\r\n</p>\r\n<p style="text-indent:2em;color:#434343;font-family:''Microsoft YaHei'', Tahoma, Helvetica, SimSun, sans-serif;font-size:16px;background-color:#FFFFFF;">\r\n	<br />\r\n</p>\r\n<p style="text-indent:2em;color:#434343;font-family:''Microsoft YaHei'', Tahoma, Helvetica, SimSun, sans-serif;font-size:16px;background-color:#FFFFFF;">\r\n	<br />\r\n</p>\r\n<p style="text-indent:2em;color:#434343;font-family:''Microsoft YaHei'', Tahoma, Helvetica, SimSun, sans-serif;font-size:16px;background-color:#FFFFFF;">\r\n	<img src="http://www.v5jr.com/data/upfiles/image/20141124/20141124142525_62161.jpg" alt="" />\r\n</p>\r\n<p style="text-indent:2em;color:#434343;font-family:''Microsoft YaHei'', Tahoma, Helvetica, SimSun, sans-serif;font-size:16px;background-color:#FFFFFF;">\r\n	<br />\r\n</p>\r\n<p style="text-indent:2em;color:#434343;font-family:''Microsoft YaHei'', Tahoma, Helvetica, SimSun, sans-serif;font-size:16px;background-color:#FFFFFF;">\r\n	<br />\r\n</p>\r\n<p style="text-indent:2em;color:#434343;font-family:''Microsoft YaHei'', Tahoma, Helvetica, SimSun, sans-serif;font-size:16px;background-color:#FFFFFF;">\r\n	&nbsp;<img src="http://www.v5jr.com/data/upfiles/image/20141124/20141124142628_92881.jpg" alt="" />\r\n</p>\r\n<p style="text-indent:2em;color:#434343;font-family:''Microsoft YaHei'', Tahoma, Helvetica, SimSun, sans-serif;font-size:16px;background-color:#FFFFFF;">\r\n	<br />\r\n</p>\r\n<p style="text-indent:2em;color:#434343;font-family:''Microsoft YaHei'', Tahoma, Helvetica, SimSun, sans-serif;font-size:16px;background-color:#FFFFFF;">\r\n	<br />\r\n</p>\r\n<p style="text-indent:2em;color:#434343;font-family:''Microsoft YaHei'', Tahoma, Helvetica, SimSun, sans-serif;font-size:16px;background-color:#FFFFFF;">\r\n	<img src="http://www.v5jr.com/data/upfiles/image/20141124/20141124143719_96011.jpg" alt="" />\r\n</p>', '<p style="text-indent:2em;color:#434343;font-family:''Microsoft YaHei'', Tahoma, Helvetica, SimSun, sans-serif;font-size:16px;background-color:#FFFFFF;">\r\n	公司名称：深圳威武金融服务有限公司\r\n</p>\r\n<p style="text-indent:2em;color:#434343;font-family:''Microsoft YaHei'', Tahoma, Helvetica, SimSun, sans-serif;font-size:16px;background-color:#FFFFFF;">\r\n	地址：深圳市福田区彩田南路中深花园大厦A栋A908\r\n</p>\r\n<p style="text-indent:2em;color:#434343;font-family:''Microsoft YaHei'', Tahoma, Helvetica, SimSun, sans-serif;font-size:16px;background-color:#FFFFFF;">\r\n	客户群：5201555\r\n</p>\r\n<p style="text-indent:2em;color:#434343;font-family:''Microsoft YaHei'', Tahoma, Helvetica, SimSun, sans-serif;font-size:16px;background-color:#FFFFFF;">\r\n	艾丽QQ：57682555\r\n</p>\r\n<p style="text-indent:2em;color:#434343;font-family:''Microsoft YaHei'', Tahoma, Helvetica, SimSun, sans-serif;font-size:16px;background-color:#FFFFFF;">\r\n	小鹰QQ：57632555\r\n</p>\r\n<p style="text-indent:2em;color:#434343;font-family:''Microsoft YaHei'', Tahoma, Helvetica, SimSun, sans-serif;font-size:16px;background-color:#FFFFFF;">\r\n	艾丽：13510287555\r\n</p>\r\n<p style="text-indent:2em;color:#434343;font-family:''Microsoft YaHei'', Tahoma, Helvetica, SimSun, sans-serif;font-size:16px;background-color:#FFFFFF;">\r\n	小鹰：13510297555\r\n</p>\r\n<p style="text-indent:2em;color:#434343;font-family:''Microsoft YaHei'', Tahoma, Helvetica, SimSun, sans-serif;font-size:16px;background-color:#FFFFFF;">\r\n	座机：0755-89356555\r\n</p>', '<table class="ke-zeroborder" border="0" cellspacing="0" cellpadding="0" width="700" style="font-family:''Microsoft YaHei'', Tahoma, Helvetica, SimSun, sans-serif;background-color:#FFFFFF;border:1px solid #F3F3F3;color:#333333;font-size:15px;">\r\n	<tbody>\r\n		<tr>\r\n			<td height="21" colspan="2" style="color:#E7323D;font-size:16px;">\r\n				<span style="font-size:14px;">1、投资人规则</span>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td width="75" align="center">\r\n				<span style="color:#E53333;font-size:14px;">费用</span>\r\n			</td>\r\n			<td width="623">\r\n				<span style="font-size:14px;">VIP免费、无充值手续费、无利息管理费，投资人无任何费用；</span>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td align="center">\r\n				<span style="font-size:14px;">回款</span>\r\n			</td>\r\n			<td>\r\n				<span style="font-size:14px;">借款标到期日当天上午10：00&nbsp;自动回款；</span>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td align="center">\r\n				<span style="font-size:14px;">逾期</span>\r\n			</td>\r\n			<td>\r\n				<span style="font-size:14px;">借款标到期日第二天(逾期当天)平台垫付本金及利息，债权归威武金融所有；</span>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td align="center">\r\n				<span style="font-size:14px;">提现</span>\r\n			</td>\r\n			<td>\r\n				<span style="font-size:14px;">七天内充值无投资提现扣除千五的手续费，其他提现免费；</span>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td align="center">\r\n				<span style="font-size:14px;">到账</span>\r\n			</td>\r\n			<td>\r\n				<span style="font-size:14px;">工作日16：30前的提现，当天到账，快慢取决于银行，推荐用招行为提现银行。 周六下午截止到16:00提现一次当天到账。 周日当天不处理提现，提现下周一到账；</span>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td colspan="2" style="color:#E7323D;font-size:16px;">\r\n				<span style="font-size:14px;">2、投资人奖励</span>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td align="center">\r\n				<span style="font-size:14px;">续投奖励</span>\r\n			</td>\r\n			<td>\r\n				<span style="font-size:14px;">回款续投奖励0.1%； (当周续投奖，下周一自动发放，续投总额低于1000元无奖励)；</span>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td align="center">\r\n				<span style="font-size:14px;">邀请奖励</span>\r\n			</td>\r\n			<td>\r\n				<span style="font-size:14px;">邀请奖励为投资好友的0.1%，通过注册链接邀请的好友并投资；</span>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td align="center">\r\n				<span style="font-size:14px;">线下充值</span>\r\n			</td>\r\n			<td>\r\n				<span style="font-size:14px;">线下充值奖励0.1%；</span>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td colspan="2" style="color:#E7323D;font-size:16px;">\r\n				<span style="font-size:14px;">3、借款人：</span>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td align="center">\r\n				<span style="font-size:14px;">费用</span>\r\n			</td>\r\n			<td>\r\n				<span style="font-size:14px;">借款成功后，本金1%-3%/月的居间服务费。</span>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>', 131, '', 0, '周小丽', 13510287555, 1418368520),
